import React, { useState } from "react";
import { login, register } from "./Database/firebaseAuth.js";

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    try {
      const user = await login(email, password);
      console.log("‚úÖ Usuario logueado:", user.uid);
      onLogin(user);
    } catch (error) {
      alert("‚ùå Error al iniciar sesi√≥n: " + error.message);
    }
  };

  const handleRegister = async () => {
    try {
      const user = await register(email, password);
      console.log("‚úÖ Usuario registrado:", user.uid);
      onLogin(user);
    } catch (error) {
      alert("‚ùå Error al registrar: " + error.message);
    }
  };

  return (
    <div className="login-container">
      <h2>üîê Login por Email</h2>
      <input
        type="email"
        placeholder="Correo"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Contrase√±a"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Iniciar sesi√≥n</button>
      <button onClick={handleRegister}>Registrarse</button>
    </div>
  );
};

export default Login;
