import React, { useState, useEffect, useRef } from "react";
import imagenes from "./assets/imagenes";
import { gsap } from "gsap"; // Importando GSAP
import "./Invit3.css";

const Invitacion3 = () => {
    const [sobreAbierto, setSobreAbierto] = useState(false);
    const sobreRef = useRef(null);
    const musicRef = useRef(null);
    const sobreParamsRef = useRef(null);

    useEffect(() => {
        if (sobreAbierto) {
            musicRef.current.play();
            loadGsap();
        }
    }, [sobreAbierto]);

    const loadGsap = () => {
        const translateY = sobreParamsRef.current?.getAttribute("translateY") || "100%";
        const duration = sobreParamsRef.current?.getAttribute("duration") || "3";

        gsap.set(".event", { y: "6vw", scale: 0 });
        gsap.set(".person", { scale: 0 });
        gsap.set(".age", { y: "14vw" });
        gsap.set(".mensaje", { scale: 0 });
        gsap.set(".tarjet", { x: "-90vw" });
        gsap.set(".date", { y: "6vw", scale: 0 });
        gsap.set(".decorations", { opacity: 0 });

        gsap.to(".date", { delay: 1.0, duration: 1, y: 0, scale: 1 });
        gsap.to(".sello", { duration: 0.5, opacity: 0 });
        gsap.to(".first", { delay: 0.0, duration: 3, y: "-100%" });
        gsap.to(".second", {
            delay: 0.0,
            duration: duration,
            y: translateY,
            onComplete: () => {
                sobreRef.current.remove();
            },
        });

        gsap.to(".age", { delay: 2.8, duration: 2, y: 0 });
        gsap.to(".decorations", { delay: 4.3, duration: 1.5, opacity: 1 });

        gsap.timeline()
            .to(".event", { delay: 1.8, duration: 0.6, rotation: 360, y: 0, scale: 1 })
            .to(".person", { duration: 0.7, scale: 1 })
            .to(".tarjet", { duration: 0.7, x: 0 })
            .to(".person-name", { duration: 0.7, scale: 1 })
            .to(".mensaje", { duration: 0.7, scale: 1 });
    };

    return (
        <>
            {!sobreAbierto ? (
                <div className="sobre" ref={sobreRef}>
                    <div className="first" id="sobre_hoja1"></div>
                    <button className="sello" onClick={() => setSobreAbierto(true)}>
                        Abrir Invitación
                    </button>
                    <div className="second" id="sobre_hoja2"></div>
                </div>
            ) : (
                <div className="contenido">
                    <div className="top">
                        <img className="top-img" src={imagenes.flores2} alt="Decoración superior" />
                    </div>
                    <div className="bottom">
                        <img className="bottom-img" src={imagenes.flores} alt="Decoración inferior" />
                    </div>
                </div>
            )}
            <audio ref={musicRef} id="music" src="./assets/music.mp3"></audio>
            <div ref={sobreParamsRef} translateY="100%" duration="3"></div>
        </>
    );
};

export default Invitacion3;
