import React, { useState } from "react";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { setDoc, doc, getDoc } from "firebase/firestore";
import { auth, db } from "./firebaseConfig";

const RegistroCliente = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nombreCliente, setNombreCliente] = useState("");
  const [categoria, setCategoria] = useState("XV A√±os"); // Puedes hacer esto din√°mico

  const generarNombreClienteUnico = async (categoria, nombreClienteOriginal) => {
    const nombreBase = nombreClienteOriginal.trim().replace(/\s+/g, "-");
    let nombreFinal = `${nombreBase}${Math.floor(1000 + Math.random() * 9000)}`;

    const ref = doc(db, "Clientes", "Categorias", categoria, nombreFinal);
    const snap = await getDoc(ref);

    // üåÄ Si ya existe, repite hasta que encuentre uno libre
    while (snap.exists()) {
        nombreFinal = `${nombreBase}${Math.floor(1000 + Math.random() * 9000)}`;
        const newRef = doc(db, "Clientes", "Categorias", categoria, nombreFinal);
        snap = await getDoc(newRef);
    }

    return nombreFinal;
    };


  const registrarCliente = async (email, categoria, nombreCliente) => {
    const ref = doc(db, "Clientes", "Categorias", categoria, nombreCliente);
    const snap = await getDoc(ref);

    if (snap.exists()) {
        throw new Error(`‚ö†Ô∏è Ya existe un cliente llamado "${nombreCliente}" en la categor√≠a "${categoria}".`);
    }

    await setDoc(ref, {
        nombreCliente,
        categoria,
        correo: email,
        rutaPersonalizada: `/${categoria}/${nombreCliente}/invitaciones`,
    });

    console.log(`‚úÖ Cliente guardado en /Clientes/Categorias/${categoria}/${nombreCliente}`);
    };


  const handleRegister = async () => {
    if (!email || !password || !nombreCliente || !categoria) {
      alert("‚ö†Ô∏è Por favor, completa todos los campos.");
      return;
    }

    try {
      const nombreClienteFinal = await generarNombreClienteUnico(categoria, nombreCliente);
      const userCred = await createUserWithEmailAndPassword(auth, email, password);
      await registrarCliente(email, categoria, nombreClienteFinal);
      alert("‚úÖ Registro exitoso");
      onLogin(userCred.user);
    } catch (err) {
      alert("‚ùå Error al registrarse: " + err.message);
    }
  };
  

  return (
    <div>
      <h2>üìù Registro de Cliente</h2>
      <input
        value={nombreCliente}
        onChange={(e) => setNombreCliente(e.target.value)}
        placeholder="Nombre del cliente (ej. Samuel)"
      />
      <input
        value={categoria}
        onChange={(e) => setCategoria(e.target.value)}
        placeholder="Categor√≠a del evento (ej. XV A√±os)"
      />
      <input
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Correo"
      />
      <input
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Contrase√±a"
        type="password"
      />
      <button onClick={handleRegister}>Registrarse</button>
    </div>
  );
};

export default RegistroCliente;
