import React, { useState, useEffect } from "react";
import { collection, getDocs, deleteDoc, doc } from "firebase/firestore";
import { db } from "../Database/firebaseConfig";

const MostrarInvitaciones = ({ nombreCliente, setListaInvitaciones }) => {
  const [invitaciones, setInvitaciones] = useState([]);

  useEffect(() => {
    const obtenerInvitaciones = async () => {
      try {
        const nombreClienteGuardado = localStorage.getItem("nombreCliente") || "Invitado";
    
        // 📌 Verificamos que `nombreClienteGuardado` tiene valor correcto
        console.log("🧐 Nombre cliente guardado:", nombreClienteGuardado);
    
        // 📌 Ajustamos la referencia a la subcolección `invitaciones`
        const ref = collection(db, "clientes", nombreClienteGuardado, "invitaciones");
        
        const querySnapshot = await getDocs(ref);
        const data = querySnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data(),
        }));
    
        console.log("✅ Invitaciones obtenidas:", data);
        setInvitaciones(data);
        setListaInvitaciones(data);
      } catch (error) {
        console.error("❌ Error al obtener las invitaciones:", error);
      }
    };    
  
    obtenerInvitaciones();
  }, [nombreCliente]);  // ✅ Eliminamos `setListaInvitaciones` de la lista de dependencias
  

  const eliminarInvitacion = async (id) => {
    try {
      const nombreClienteGuardado = localStorage.getItem("nombreCliente") || "Invitado";
  
      // 📌 La referencia debe apuntar a `clientes/{nombreCliente}`
      const docRef = doc(db, "clientes", nombreClienteGuardado);
      
      await deleteDoc(docRef);
      console.log(`✅ Invitación de ${nombreClienteGuardado} eliminada correctamente.`);
      
      // 📌 Actualizar la tabla sin recargar Firebase
      setInvitaciones((prev) => prev.filter((inv) => inv.id !== id));
      setListaInvitaciones((prev) => prev.filter((inv) => inv.id !== id));
    } catch (error) {
      console.error("❌ Error al eliminar la invitación:", error);
    }
  };
    
  

  return (
    <div>
      <h2>Lista de Invitaciones Guardadas</h2>
      <table border="1">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Adultos</th>
            <th>Niños</th>
            <th>Enlace</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {invitaciones.length > 0 ? (
            invitaciones.map((invitacion) => (
              <tr key={invitacion.id}>
                <td>{invitacion.nombre}</td>
                <td>{invitacion.invitadosAdultos}</td>
                <td>{invitacion.invitadosNiños}</td>
                <td><a href={invitacion.link} target="_blank" rel="noopener noreferrer">Ver Invitación</a></td>
                <td><button onClick={() => eliminarInvitacion(invitacion.id)}>❌ Eliminar</button></td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">No hay invitaciones registradas aún.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default MostrarInvitaciones;
