import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { db } from "./Database/firebaseConfig";
import { collection, doc, setDoc, addDoc } from "firebase/firestore";
import FotoSection from "./FotoSection";
import FormGen from "./componentes/FormGen";
import Invitacion from "./Invitacion";
import imagenes from "./assets/imagenes";
import "./invit.css";
import SendInvitacion from "./Database/SendInvitacion";
import MostrarInvitaciones from "./Database/MostrarInvitaciones";

const App = () => {
  const [nombre, setNombre] = useState(() => {
    return localStorage.getItem("nombreCliente") || "Invitado";
  });
  const [totalInvitados, setTotalInvitados] = useState(1);
  const [linkGenerado, setLinkGenerado] = useState("");
  const [linkAcortado, setLinkAcortado] = useState("");
  const [mostrarMensaje, setMostrarMensaje] = useState(false);
  const [listaInvitaciones, setListaInvitaciones] = useState([]);



  useEffect(() => {
    if (linkGenerado) {
      setMostrarMensaje(true);

      // 📌 Ocultar el mensaje después de 5 segundos
      const timer = setTimeout(() => {
        setMostrarMensaje(false);
      }, 5000);

      return () => clearTimeout(timer);
    }
  }, [linkGenerado]);

  useEffect(() => {
    const manejarScroll = () => {
      setMostrarMensaje(false); // 📌 Ocultar cuando el usuario hace scroll
    };

    window.addEventListener("scroll", manejarScroll);
    return () => window.removeEventListener("scroll", manejarScroll);
  }, []);

  const actualizarDatos = async (nombreCliente, nuevoNombre, nuevaCantidadAdultos, nuevaCantidadNiños, incluirNiños) => {
    if (!nombreCliente || typeof nombreCliente !== "string" || nombreCliente.trim() === "") {
      console.error("❌ Error: `nombreCliente` no tiene un valor válido.");
      return;
    }

    let nombreLimpio = typeof nuevoNombre === "string" ? nuevoNombre.replace(/\s+/g, " ").trim() : String(nuevoNombre).trim();
    const idInvitacion = nombreCliente.replace(/\s+/g, "-").toLowerCase();  // ✅ Usa el nombre como ID
    const nuevoLink = `https://tuweb.com/invitaciones/${idInvitacion}`;
    const totalInvitadosCalculado = nuevaCantidadAdultos + (incluirNiños ? nuevaCantidadNiños : 0);

    // 📌 Corregir la asignación de estados
    setNombre(nombreCliente);  // ✅ Guarda el nombre correctamente en el estado
    console.log("✅ Estado actualizado en App.js:", nombreCliente);  // 📌 Verificación en consola
    setInvitadosAdultos(nuevaCantidadAdultos);
    setInvitadosNiños(nuevaCantidadNiños); // ✅ Guarda los niños correctamente
    setTotalInvitados(totalInvitadosCalculado); // ✅ Corrige el cálculo del total
    setLinkGenerado(nuevoLink);
    setMostrarMensaje(true);  // ✅ Activa el mensaje inmediatamente al crear la invitación
    setTimeout(() => setMostrarMensaje(false), 5000);  // ✅ Ocultar después de 5 segundos  

    try {
      await setDoc(doc(db, "clientes", nombreCliente), {  
        id: idInvitacion,  // ✅ Guarda el ID como nombre del invitado
        nombre: nombreCliente,  // ✅ Guarda el nombre tal como se ingresó en el formulario
        invitadosAdultos: nuevaCantidadAdultos,
        invitadosNiños: nuevaCantidadNiños,
        totalInvitados: totalInvitadosCalculado,
        link: nuevoLink,
        timestamp: new Date(),
      });

      const response = await axios.get(`https://tinyurl.com/api-create.php?url=${nuevoLink}`);
      setLinkAcortado(response.data);
    } catch (error) {
      console.error("❌ Error al guardar la invitación:", error);
    }
  };

  const handleCompartir = () => {
    if (!linkGenerado) {
      alert("Primero genera la invitación antes de compartirla.");
      return;
    }

    // 📌 Copia el enlace al portapapeles
    navigator.clipboard.writeText(linkGenerado);

    // 📌 Enviar el enlace por WhatsApp
    const mensajeWhatsApp = `¡Hola! Te comparto la invitación: ${linkGenerado}`;
    const urlWhatsApp = `https://api.whatsapp.com/send?phone=+5619060019&text=${encodeURIComponent(mensajeWhatsApp)}`;

    window.open(urlWhatsApp, "_blank");
  };

  const copiarAlPortapapeles = () => {
    navigator.clipboard.writeText(linkAcortado || linkGenerado);
    alert("Enlace copiado al portapapeles.");
  };

  return (
    <div>
      {/* 📌 Mostrar el mensaje en un encabezado solo si `mostrarMensaje` está activado */}
      {mostrarMensaje && (
        <header className="invitacion-creada">
          <h1>🎉 Tu invitación ha sido creada 🎉</h1>
        </header>
      )}


      <FormGen 
        actualizarDatos={actualizarDatos} 
        handleCompartir={handleCompartir} 
        linkGenerado={linkGenerado}
      />

      <MostrarInvitaciones nombreCliente={nombre} setListaInvitaciones={setListaInvitaciones} />

      <Invitacion nombreInvitacion={nombre} totalInvitadosInvitacion={totalInvitados} />
    </div>
  );
};

export default App;
