import React, { useState } from "react";
import { db } from "./firebaseConfig";
import { collection, getDocs } from "firebase/firestore";
import { signInWithEmailAndPassword, signOut } from "firebase/auth";
import { auth } from "./firebaseConfig";
import "./Login.css";

const Login = ({ onLogin, setVerificandoSesion, setUsuarioActual }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mostrarPassword, setMostrarPassword] = useState(false);

  const obtenerDatosCliente = async (email) => {
    const categorias = ["XV A√±os", "Eventos Empresariales", "Bodas"];
    for (const categoria of categorias) {
      const clientesRef = collection(db, "Clientes", "Categorias", categoria);
      const snapshot = await getDocs(clientesRef);

      for (const docSnap of snapshot.docs) {
        const data = docSnap.data();
        if (data.correo === email) {
          return data; // ‚úÖ contiene rutaPersonalizada y fechaLimite
        }
      }
    }
    throw new Error("Cliente no registrado en Firestore.");
  };

  const handleLogin = async () => {
    try {
      setVerificandoSesion(true);
      const userCred = await signInWithEmailAndPassword(auth, email, password);
      const user = userCred.user;

      const clienteData = await obtenerDatosCliente(user.email);

      // ‚úÖ Validar si la fechaLimite existe y est√° bien formateada
      const limite = clienteData?.fechaLimite
        ? new Date(clienteData.fechaLimite)
        : null;
      const ahora = new Date();
      const tieneAcceso = limite && ahora <= limite;

      // ‚úÖ Enviar al padre toda la info necesaria
      const usuarioExtendido = {
        ...user,
        ...clienteData,
        fechaLimite: clienteData.fechaLimite,
        tieneAcceso,
      };

      setUsuarioActual(usuarioExtendido);
      onLogin(usuarioExtendido);
      setVerificandoSesion(false);
      window.location.href = clienteData.rutaPersonalizada;
    } catch (err) {
      alert("‚ùå El correo o contrase√±a son incorrectos");
      setVerificandoSesion(false);
    }
  };

  return (
  <div className="fondo-app">
    {verificandoSesion ? (
      <div className="loader-contenedor">
        <span className="loader"></span>
        <p>Cargando...</p>
      </div>
    ) : !usuarioActual ? (
      <>
        <Login
          onLogin={(user) => setUsuarioActual(user)}
          setVerificandoSesion={setVerificandoSesion}
          setUsuarioActual={setUsuarioActual}
        />
        <RegistroCliente
          onLogin={(user) => {
            setUsuarioActual(user);
            setVerificandoSesion(false);
          }}
        />
      </>
    ) : (
      <div className="App">
        {mostrarMensaje && (
          <header className="invitacion-creada">
            <h1>üéâ Tu invitaci√≥n ha sido creada üéâ</h1>
          </header>
        )}

        {menuVisible && (
          <div className="sidebar menu">
            <div className="logo_content">
              <div className="logo">
                <i className="fa-solid fa-user"></i>
                <p className="logo_name">{usuarioActual.email}</p>
              </div>
              <i
                className="fa-solid fa-bars"
                id="btn"
                onClick={() => setMenuVisible(false)}
              ></i>
            </div>

            <ul className="nav">
              <li>
                <a href="#">
                  <i className="fa-solid fa-key"></i>
                  <span className="link_name">Cambiar Contrase√±a</span>
                </a>
              </li>
              <li>
                <a href="#">
                  <i className="fa-solid fa-arrow-right-from-bracket"></i>
                  <span className="link_name" onClick={handleLogout}>
                    Cerrar sesi√≥n
                  </span>
                </a>
              </li>
            </ul>
          </div>
        )}

        {!menuVisible && (
          <div className="toggle-menu">
            <i
              className="fa-solid fa-bars"
              onClick={() => setMenuVisible(true)}
            ></i>
          </div>
        )}

        {/* ‚úÖ Validaci√≥n din√°mica por fecha */}
        {usuarioActual?.fechaLimite &&
        new Date() <= new Date(usuarioActual.fechaLimite) ? (
          <FormGen
            actualizarDatos={actualizarDatos}
            handleCompartir={handleCompartir}
            linkGenerado={linkGenerado}
          />
        ) : (
          <div className="bloqueado">
            üö´ Tu acceso ha expirado. Por favor, contacta para renovarlo.
          </div>
        )}

        <MostrarInvitaciones
          CATEGORIA_EVENTO={CATEGORIA_EVENTO}
          NOMBRE_CLIENTE={NOMBRE_CLIENTE}
          setListaInvitaciones={setListaInvitaciones}
          setLinkPrevisualizacion={setLinkPrevisualizacion}
        />

        {linkPrevisualizacion && (
          <section className="contenedor-iframe">
            <h2 className="titulo-iframe">üîç Vista previa de las invitaciones</h2>
            <iframe
              src={linkPrevisualizacion}
              title="Previsualizaci√≥n de las invitaciones"
              className="iframe-preview"
              allow="fullscreen"
              loading="lazy"
              sandbox="allow-scripts allow-same-origin"
            />
          </section>
        )}
      </div>
    )}
  </div>
);
};

export default Login;
