import React, { useState, useEffect } from "react";
import { collection, onSnapshot, deleteDoc, doc } from "firebase/firestore";
import { db } from "../Database/firebaseConfig";

const MostrarInvitaciones = ({
  CATEGORIA_EVENTO,
  NOMBRE_CLIENTE,
  setListaInvitaciones,
  setLinkPrevisualizacion
}) => {
  const [invitaciones, setInvitaciones] = useState([]);

  useEffect(() => {
    if (!CATEGORIA_EVENTO || !NOMBRE_CLIENTE) return;

    const invitacionesRef = collection(db, CATEGORIA_EVENTO, NOMBRE_CLIENTE, "invitaciones");

    const unsubscribe = onSnapshot(invitacionesRef, (querySnapshot) => {
      const data = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setInvitaciones(data);
      setListaInvitaciones && setListaInvitaciones(data);
    });

    return () => unsubscribe();
  }, [CATEGORIA_EVENTO, NOMBRE_CLIENTE]);

  const eliminarInvitacion = async (id) => {
    if (!CATEGORIA_EVENTO || !NOMBRE_CLIENTE) return;
    try {
      const idNormalizado = id.replace(/\s+/g, "").toLowerCase();
      const docRef = doc(db, CATEGORIA_EVENTO, NOMBRE_CLIENTE, "invitaciones", idNormalizado);
      await deleteDoc(docRef);
    } catch (error) {
      console.error("❌ Error al eliminar la invitación:", error);
    }
  };

  return (
    <div className="desing-table">
      <h2 className="table">Número Total de invitados:</h2>
      <table className="tabla-invitaciones">
        <thead>
          <tr>
            <th>Niños</th>
            <th>Adultos</th>
            <th>Total</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{invitaciones.reduce((t, i) => t + (i.invitadosNiños || 0), 0)}</td>
            <td>{invitaciones.reduce((t, i) => t + (i.invitadosAdultos || 0), 0)}</td>
            <td>{invitaciones.reduce((t, i) => t + ((i.invitadosNiños || 0) + (i.invitadosAdultos || 0)), 0)}</td>
          </tr>
        </tbody>
      </table>

      <h2 className="table">Lista de Invitaciones Guardadas</h2>

      {!CATEGORIA_EVENTO || !NOMBRE_CLIENTE ? (
        <p>⚠️ No hay un evento definido para mostrar las invitaciones.</p>
      ) : (
        <div className="tabla-grid">
          <div className="fila encabezado">
            <div>Nombre</div>
            <div>Adultos</div>
            <div>Niños</div>
            <div>Enlace</div>
            <div>Acciones</div>
          </div>

          {invitaciones.length > 0 ? (
            invitaciones.map((invitacion) => (
              <div className="fila datos" key={invitacion.id}>
                <div>{invitacion.nombre}</div>
                <div>{invitacion.invitadosAdultos}</div>
                <div>{invitacion.invitadosNiños}</div>
                <div>
                  <button onClick={() => setLinkPrevisualizacion(invitacion.link)}>👁️ Ver</button>
                </div>
                <div>
                  <button onClick={() => eliminarInvitacion(invitacion.id)}>❌ Eliminar</button>
                </div>
              </div>
            ))
          ) : (
            <div className="fila">
              <div style={{ gridColumn: "1 / -1", textAlign: "center", padding: "1rem" }}>
                No hay invitaciones registradas aún.
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default MostrarInvitaciones;
