import React, { useEffect, useRef, useState } from "react";

function AnimacionScroll({ className, children }) {
  const ref = useRef(null);
  const [visible, setVisible] = useState(false);
  const lastScrollY = useRef(window.scrollY);
  const lastVisible = useRef(false); // ðŸ”¥ Variable para evitar reinicios no deseados

  useEffect(() => {
    const observer = new IntersectionObserver(([entry]) => {
      const scrollDown = window.scrollY > lastScrollY.current;
      lastScrollY.current = window.scrollY;

      if (scrollDown && entry.isIntersecting && !lastVisible.current) {
        setVisible(true); // ðŸ”¥ Activa la animaciÃ³n solo si aÃºn no es visible
        lastVisible.current = true; // âœ… Marcamos como "visible" para evitar repeticiones
      } else if (!scrollDown && entry.boundingClientRect.bottom < window.innerHeight * 0.2) {
        setVisible(false); // ðŸ”¥ Solo desaparece si se va completamente de pantalla por abajo
        lastVisible.current = false; // ðŸ”¥ Restablece estado para que se pueda reactivar despuÃ©s
      }
    }, { threshold: 0.5 }); // ðŸ”¥ Mayor precisiÃ³n en la activaciÃ³n

    if (ref.current) observer.observe(ref.current);

    return () => observer.disconnect();
  }, []);

  return (
    <div ref={ref} className={`${className} ${visible ? "visible" : "hidden"}`}>
      {children}
    </div>
  );
}

export default AnimacionScroll;
