import React, { useState } from "react";
    import "./Invit2.css";
import FotoSection from "./FotoSection";
import imagenes from "./assets/imagenes";

const Invitacion = () => {
  const [sobreAbierto, setSobreAbierto] = useState(false);

  return (
    <div className="contenido">
      {!sobreAbierto && (
        <div className="sobre">
          <div className="up" id="sobre_hoja1"></div>
          <button className="blason" onClick={() => setSobreAbierto(true)}>
            Abrir Invitación
          </button>
          <div className="down" id="sobre_hoja2"></div>
        </div>
      )}

      {sobreAbierto && (
        <>
          {/* MARK: Decoraciones */}
          <div className="top">
            <img className="top-img" src="../../../Decoraciones/373.png" alt="Decoración superior" />
          </div>
          <div className="bottom">
            <img className="bottom-img" src="../../../Decoraciones/374.png" alt="Decoración inferior" />
          </div>

          {/* MARK: Nombre */}
          <div className="encabezado">
            <h1 className="evento">Mis XV</h1>
            <h2 className="nombre-principal">Mayte Ximena</h2>
          </div>

          {/* MARK: Foto */}
          <div className="foto aparecer" style={{ marginBottom: "-7%", marginTop: "-2%" }}>
            <img className="principal" src="foto.jpg" alt="Foto de la festejada" />
            <img className="aro-foto" src="../../../Decoraciones/foto/oro/aro.png" alt="Aro decorativo estático" />
            <img className="aro" src="../../../Decoraciones/foto/oro/aro.gif" alt="Aro decorativo animado" />
          </div>

          {/* MARK: Frases */}
          <div className="frase aparecer">
            "Mis XV años serán un sueño hecho realidad, y quiero que tú formes parte de este capítulo único en mi vida"
          </div>

          {/* MARK: Icono */}
          <div 
            className="icono-inv"
            data-icono="ornamento2"
            style={{
              marginTop: "3%",
              marginBottom: "-5%",
              width: "70%",
              maskImage: "url('../../../Iconos/ornamento2.png')"
            }}
          />
        </>
      )}
    </div>
  );
};

export default Invitacion;
