import React from "react";
import imagenes from "./assets/imagenes"; // ✅ Importamos el álbum

const eventos = [
  { nombre: "Misa", hora: "3:45 PM", icono: "iglesia" },
  { nombre: "Recepción", hora: "5:10 PM", icono: "recepcion" },
  { nombre: "Comida", hora: "5:45 PM", icono: "comida" },
  { nombre: "Vals", hora: "9:30 PM", icono: "vals" },
  { nombre: "Baile", hora: "10:30 PM", icono: "baile" },
  { nombre: "Fin del Evento", hora: "1:00 AM", icono: "fin" },
];

const Itinerario = () => {
  return (
    <div className="itinerario show-p-y">
      <h3>Programa del Evento</h3>
      <div>
        {eventos.map((evento, index) => {
          const posicion = index % 2 === 0 ? "izquierda" : "derecha";
          return (
            <div key={index} className={`evento ${posicion}`}>
              {posicion === "izquierda" ? (
                <>
                  <div className="icono show-n-x">
                    <div className="circulo">
                      <img className="icono-itinerario" src={imagenes[evento.icono]} alt={evento.nombre} />
                    </div>
                  </div>
                  <div className="item show-p-x">
                    <h4 className="nombre">{evento.nombre}</h4>
                    <p className="hora">{evento.hora}</p>
                  </div>
                </>
              ) : (
                <>
                  <div className="item show-n-x">
                    <h4 className="nombre">{evento.nombre}</h4>
                    <p className="hora">{evento.hora}</p>
                  </div>
                  <div className="icono show-p-x">
                    <div className="circulo">
                      <img className="icono-itinerario" src={imagenes[evento.icono]} alt={evento.nombre} />
                    </div>
                  </div>
                </>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Itinerario;
