import React, { useState, useEffect } from "react";
import axios from "axios";
import { db } from "./Database/firebaseConfig";
import { collection, addDoc, getDocs } from "firebase/firestore";
import FotoSection from "./FotoSection";
import FormGen from "./componentes/FormGen";
import imagenes from "./assets/imagenes";
import "./invit.css";

const App = () => {
  const [nombre, setNombre] = useState("Invitado");
  const [invitadosAdultos, setInvitadosAdultos] = useState(1);
  const [invitadosNi√±os, setInvitadosNi√±os] = useState(0);
  const [incluirNi√±os, setIncluirNi√±os] = useState(false);
  const [totalInvitados, setTotalInvitados] = useState(1); // üÜï Nuevo estado para reflejar la suma
  const [linkGenerado, setLinkGenerado] = useState("");
  const [linkAcortado, setLinkAcortado] = useState("");

  // üÜï Funci√≥n para generar un ID autoincrementable
  const generarIDInvitacion = async (tipoDise√±o, nombreCliente) => {
    const querySnapshot = await getDocs(collection(db, "invitaciones"));
    const cantidadUsada = querySnapshot.docs.filter(doc => doc.data().tipoDise√±o === tipoDise√±o).length + 1;
    return `${tipoDise√±o}${cantidadUsada}${nombreCliente.replace(/\s+/g, "")}`;
  };

  // üìå Se actualiza `totalInvitados` din√°micamente cuando cambian adultos o ni√±os
  useEffect(() => {
    setTotalInvitados(invitadosAdultos + (incluirNi√±os ? invitadosNi√±os : 0));
  }, [invitadosAdultos, invitadosNi√±os, incluirNi√±os]);

  // üìå Funci√≥n para actualizar datos y guardar en Firebase
  const actualizarDatos = async (nuevoNombre, nuevaCantidadAdultos, nuevaCantidadNi√±os, incluirNi√±os) => {
    let nombreLimpio = nuevoNombre.replace(/\s+/g, " ").trim();
    let nombreFinal = nombreLimpio.length > 11 ? nombreLimpio + ",\n" : nombreLimpio;
    const idInvitacion = await generarIDInvitacion("RJ", nuevoNombre);
    const nuevoLink = `https://tuweb.com/invitaciones/${idInvitacion}`;

    setNombre(nombreFinal);
    setInvitadosAdultos(nuevaCantidadAdultos);
    setInvitadosNi√±os(incluirNi√±os ? nuevaCantidadNi√±os : 0);
    setLinkGenerado(nuevoLink);

    try {
      await addDoc(collection(db, "invitaciones"), {
        id: idInvitacion,
        nombre: nombreFinal,
        invitadosAdultos: nuevaCantidadAdultos,
        invitadosNi√±os: incluirNi√±os ? nuevaCantidadNi√±os : 0,
        totalInvitados: nuevaCantidadAdultos + (incluirNi√±os ? nuevaCantidadNi√±os : 0), // ‚úÖ Se almacena correctamente en Firebase
        link: nuevoLink,
        timestamp: new Date(),
      });

      // üìå Acortar el enlace con TinyURL
      const response = await axios.get(`https://tinyurl.com/api-create.php?url=${nuevoLink}`);
      setLinkAcortado(response.data);
    } catch (error) {
      console.error("Error al guardar la invitaci√≥n:", error);
    }
  };

  // üìå Funci√≥n para copiar el enlace
  const copiarAlPortapapeles = () => {
    navigator.clipboard.writeText(linkAcortado || linkGenerado);
    alert("Enlace copiado al portapapeles.");
  };

  return (
    <div>
      <FormGen actualizarDatos={actualizarDatos} />

      {linkGenerado && (
        <div>
          <p>
            Tu invitaci√≥n ha sido generada:{" "}
            <a href={linkAcortado || linkGenerado} target="_blank" rel="noopener noreferrer">
              {linkAcortado || linkGenerado}
            </a>
          </p>
          <button onClick={copiarAlPortapapeles}>üìã Copiar enlace</button>
        </div>
      )}

      <FotoSection
        imageSrc={imagenes.img9}
        buttonText1="Confirmar asistencia"
        buttonLink1="https://api.whatsapp.com/send?phone=+525619060019&text=Gracias por invitarme!"
        buttonText2="No puedo asistir"
        buttonLink2="https://api.whatsapp.com/send?phone=+525619060019&text=No podre asistir."
        invitacion={{
          nombre: `${nombre} est√°s invitado a mi Fiesta`,
          invitados: `Invitaci√≥n para ${totalInvitados} personas`, // ‚úÖ Ahora se actualiza din√°micamente
        }}
      />
    </div>
  );
};

export default App;
