import imagenes from "./assets/imagenes";
import Itinerario from "./Itinerario";
import Countdown from "./Countdown";
import Galeria from "./Galeria";

// Componente Familia
const Familia = ({ titulo, nombres }) => (
  <div className="familia aparecer">
    <h3>{titulo}</h3>
    {nombres.map((nombre, index) => (
      <p key={index} className="nombre">{nombre}</p>
    ))}
  </div>
);

// Componente Fecha
const Fecha = ({ dia, mes, anio }) => (
  <div className="fecha">
    <p className="dia">{dia}</p>
    <div className="barra"></div>
    <p className="mes">{mes}</p>
    <div className="barra"></div>
    <p className="anio">{anio}</p>
  </div>
);

// Componente Ubicacion
const Ubicacion = ({ titulo, direccion, hora, imagen, link }) => (
  <a className="ubicacion show-p-y" href={link} target="_blank" rel="noopener noreferrer">
    <div>
        <img className="foto aparecer" src={imagen} alt={`Ubicación: ${titulo}`} />
      <p className="lugar">{titulo}</p>
      <span className="direccion">{direccion}</span>
      <span className="hora aparecer">{hora}</span>
      <div className="boton aparecer">Ir al Mapa</div>
    </div>
  </a>
);

// Componente Buzón de Deseos
const BuzonDeDeseos = () => (
  <div id="whatsappLink" className="extra aparecer show-p-y">
    <h3>Buzón de Deseos</h3>
    <p className="texto" style={{ width: "90%" }}>Si quieres dejarme un lindo mensaje por mis XV, puedes hacerlo escribiéndome un mensaje:</p>
    <textarea className="mensaje" id="mensajeInput" placeholder="Escribe tu mensaje aquí"></textarea>
    <div className="boton aparecer" style={{ width: "30%" }} onClick={() => enviarMensajeCA("521", "5538795963")}>Enviar Mensaje</div>
  </div>
);

// Componente Vestimenta
const Vestimenta = () => (
  <div className="extra aparecer show-p-y">
    <h3>Código de Vestimenta</h3>
    <div className="icono-inv" data-icono="vestimenta"></div>
    <p className="texto">Vestimenta Formal</p>
  </div>
);

// Componente Información Importante
const InformacionImportante = () => (
  <div className="extra aparecer show-p-y">
    <h3>Información Importante</h3>
    <p className="importante">❖ El color verde queda reservado exclusivamente para la Quinceañera.</p>
  </div>
);

// Componente Confirmación de Asistencia
const buttonText1 = "Confirmar asistencia";
const buttonLink1 = "https://wa.me/521XXXXXXXXXX?text=Confirmo asistencia";

const buttonText2 = "No puedo asistir";
const buttonLink2 = "https://wa.me/521XXXXXXXXXX?text=No puedo asistir";

const ConfirmacionAsistencia = () => (
  <section className="confirmacion-asistencia" style={{ zIndex: 4 }}>
    <h3>Confirmar Asistencia</h3>
    <form method="POST" onSubmit={validateForm}>
      <label htmlFor="familia" className="texto">Nombre y Apellido:</label>
      <input type="text" id="familia" name="familia" maxLength="20" className="familia-input" placeholder="Escribe Nombre y Apellido" required />
      <div className="botones-confirmacion">
        <a href={buttonLink1} className="boton" target="_blank" rel="noopener noreferrer">{buttonText1}</a>
        <a href={buttonLink2} className="boton" target="_blank" rel="noopener noreferrer">{buttonText2}</a>
      </div>
    </form>
  </section>
);

// Componente Despedida
const Despedida = () => (
  <div className="despedida aparecer">
    ¡Te Esperamos!
  </div>
);

// Componente Sonido
const Sonido = () => (
  <section id="fixed-items">
    <button id="toggleButton">
      <img src="../../../Iconos/sonido_verde_claro.png" id="soundIcon" className="boom no-foto" alt="Sonido" />
    </button>
    <audio id="music" autoPlay loop>
      <source src="./data/song.mp3" type="audio/mpeg" />
      Tu navegador no soporta la reproducción de audio.
    </audio>
  </section>
);

// Función de validación para la Confirmación de Asistencia
const validateForm = (event) => {
  event.preventDefault();
  const asistirSelect = document.querySelector('input[name="confirmacion"]:checked').id;
  const familiaInput = document.getElementById("familia").value;

  let mensajeCA = asistirSelect === "asistire"
    ? `Hola, soy ${familiaInput} y confirmo mi asistencia.`
    : `Hola, soy ${familiaInput} y lamentablemente, no podré asistir.`;

  enviarMensajeCA("521", "5574175702", mensajeCA);
};

const enviarMensajeCA = (lada, numero, mensajeCA) => {
  const urlCA = `https://wa.me/${lada}${numero}?text=${encodeURIComponent(mensajeCA)}`;
  window.open(urlCA, "_self");
};


// Componente Principal Invitación
const Invitacion = () => {
  const [sobreAbierto, setSobreAbierto] = useState(false);

  return (
    <>
      {!sobreAbierto && (
        <div className="sobre">
          <div className="up" id="sobre_hoja1"></div>
          <button className="blason" onClick={() => setSobreAbierto(true)}>
            Abrir Invitación
          </button>
          <div className="down" id="sobre_hoja2"></div>
        </div>
      )}

      {sobreAbierto && (
        <div className="contenido">
          {/* MARK: Decoraciones */}
          <div className="top">
            <img className="top-img" src={imagenes.flores2} alt="Decoración superior" />
          </div>
          <div className="bottom">
            <img className="bottom-img" src={imagenes.flores} alt="Decoración inferior" />
          </div>

          {/* MARK: Nombre */}
          <div className="encabezado">
            <h1 className="evento">Mis XV</h1>
            <h2 className="nombre-principal">Mayte Ximena</h2>
          </div>

          {/* MARK: Foto */}
          <div className="foto aparecer">
            <img className="principal" src={imagenes.XV} alt="Foto de la festejada" />
            <img className="aro-foto" src={imagenes.aro} alt="Aro decorativo" />
            <img className="aro" src={imagenes.aroefecto} alt="Efecto del aro" />
          </div>

          {/* MARK: Frases */}
          <div className="frase aparecer">
            "Mis XV años serán un sueño hecho realidad, y quiero que tú formes parte de este capítulo único en mi vida"
          </div>

          <div className="separador">
            <img className="icono-inv" src={imagenes.ornamentos} alt="Ornamento decorativo" />
          </div>
          
          {/* MARK: Padres y Padrinos */}
          <Familia titulo="Mis Papás" nombres={["Ivett Vizcarra Calzada", "&", "Ángel de la Cruz Ríos"]} />
          <Familia titulo="Mis Padrinos" nombres={["Luis Ángel Gutiérrez", "Nallely Vizcarra", "Víctor Vizcarra", "Arely Almaguer"]} />

          {/* MARK: Fecha */}
          <Fecha dia="26" mes="Abril" anio="2025" />

          {/* MARK: Contador Regresivo */}
          <Countdown day={26} month="Abril" year={2025} time="3:45 PM" />

          {/* MARK: Ubicaciones */}
          <Ubicacion
            titulo="Rectoría Nuestra Señora Del Refugio"
            direccion="Av. Insurgentes Manzana 001, Capula, 54603 Tepotzotlán, Méx."
            hora="3:45 PM"
            imagen={imagenes.iglesia}
            link="https://maps.app.goo.gl/vYWZwYKARzAEtoH59"
          />

          <Ubicacion
            titulo="Jardín Royal"
            direccion="C. Chilar Manzana 003, San Mateo Xoloc, 54602 Tepotzotlán, Méx."
            hora="5:10 PM"
            imagen={imagenes.salon}
            link="https://maps.app.goo.gl/R9jzNjmfxAZnvKWF9"
          />

          {/* MARK: Galería de Fotos */}
          <Galeria />

          <img
            className="foto-full aparecer show-p-y active-p-y"
            src={imagenes.princesa}
            alt="Imagen de princesa"
            style={{
              width: "20%",
              zIndex: 4,
              animation: "4s boom infinite",
              marginTop: "15%",
              marginBottom: "4%"
            }}
          />


          <div className="extra aparecer show-p-y active-p-y">
            <h3>Lluvia de Sobres</h3>
            <div
              className="icono-inv"
              data-icono="sobre"
              style={{
                maskImage: `url(${imagenes.sobre})`,
                WebkitMaskImage: `url(${imagenes.sobre})`,
                width: "10vw", // Ajusta tamaño si es necesario
                height: "10vw",
                backgroundColor: "#547B2A" // Relleno del icono
              }}
            ></div>
            <p className="texto">Es la tradición de regalar dinero en efectivo dentro de un sobre</p>
          </div>
          <div
            className="icono-inv"
            data-icono="sobre"
            style={{
              maskImage: `url(${imagenes.ornamentos})`,
              WebkitMaskImage: `url(${imagenes.ornamentos})`
            }}
          ></div>

          {/* MARK: Itinerario */}
          <Itinerario />

          {/* MARK: buzon de mensajes */}
          <BuzonDeDeseos />

          {/* MARK: Informacion Importante */}
          <InformacionImportante />

          {/* MARK: Confirmar Asistencia */}
          <ConfirmacionAsistencia />

          {/* MARK: Despedida */}
          <Despedida />

          {/* MARK: Musica */}
          <Sonido />
        </div>
      )}
    </>
  );
};

export default Invitacion;
