import React, { useState } from "react";
import { db } from "./firebaseConfig";
import { collection, doc, getDocs, setDoc } from "firebase/firestore";
import { signInWithEmailAndPassword, signOut, createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "./firebaseConfig";
import "./Login.css"

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mostrarPassword, setMostrarPassword] = useState(false);

  // âœ… Buscar ruta personalizada en Firestore
  const obtenerRutaPersonalizada = async (email) => {
    const categorias = ["XV AÃ±os", "Eventos Empresariales", "Bodas"]; // o hazlo dinÃ¡mico

    for (const categoria of categorias) {
        const clientesRef = collection(db, "Clientes", "Categorias", categoria);
        const snapshot = await getDocs(clientesRef);

        for (const docSnap of snapshot.docs) {
        const data = docSnap.data();
        if (data.correo === email) {
            return data.rutaPersonalizada;
        }
        }
    }

    throw new Error("Cliente no registrado en Firestore.");
    };

  const handleLogin = async () => {
    try {
      const userCred = await signInWithEmailAndPassword(auth, email, password);
      const userEmail = userCred.user.email;
      const ruta = await obtenerRutaPersonalizada(userEmail);

      onLogin(userCred.user);
      window.location.href = ruta;
    } catch (err) {
      alert("âŒ Error al iniciar sesiÃ³n o encontrar la ruta: " + err.message);
    }
  };

  const handleLogout = () => {
    signOut(auth).then(() => {
      alert("ðŸ‘‹ SesiÃ³n cerrada");
      onLogin(null);
    });
  };

  return (
    <div className="wrapper">
    <form>
      <h2>Iniciar sesiÃ³n</h2>

      <div className="input-field">
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <label>Correo</label>
      </div>

      <div className="input-field">
        <input
          type={mostrarPassword ? "text" : "password"}
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <label>ContraseÃ±a</label>
      </div>

      <div className="forget">
        <label>
          <input
            id="remember"
            type="checkbox"
            onClick={() => setMostrarPassword((prev) => !prev)}
          />
          <p>{mostrarPassword ? "Ocultar contraseÃ±a" : "Ver contraseÃ±a"}</p>
        </label>
        <a href="#">Â¿Olvidaste tu contraseÃ±a?</a>
      </div>

      <button type="button" onClick={handleLogin}>
        Iniciar sesiÃ³n
      </button>

      <p className="register">Â¿No tienes cuenta? <a href="#">RegÃ­strate aquÃ­</a></p>
    </form>
  </div>
  );
};

export default Login;
