import React, { useRef, useState, useEffect } from "react";
import imagenes from "../assets/imagenes"; // Asegúrate de tener soundOnIcon y muteIcon definidos
import "../invit-deluxe/Invit2.css";

const AudioController = () => {
  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(true);
  const [icon, setIcon] = useState(imagenes.soundOnIcon); // icono inicial

  useEffect(() => {
    const audio = audioRef.current;
    if (audio) {
      audio.volume = 0.7; // Ajusta el volumen al montar
    }
  }, []);

  const toggleMusic = () => {
    const audio = audioRef.current;
    if (!audio) return;

    if (isPlaying) {
      audio.pause();
      setIcon(imagenes.muteIcon);
    } else {
      audio.play().catch((err) => {
        if (err.name !== "AbortError") {
          console.error("Error al reproducir el audio:", err);
        }
      });
      setIcon(imagenes.soundOnIcon);
    }
    setIsPlaying(!isPlaying);
  };

  return (
    <section id="fixed-items">
      <button
  id="toggleButton"
  onClick={(e) => {
    toggleMusic();
    e.currentTarget.blur(); // Esto elimina el focus visual
  }}
>
        <img
          id="soundIcon"
          src={icon}
          alt="Sound Icon"
          className="print"
        />
      </button>
      <audio ref={audioRef} id="music" autoPlay loop>
        <source src="/assets/joansebastian-masalladeelsol.mp3" type="audio/mpeg" />
        Tu navegador no soporta la reproducción de audio.
      </audio>
    </section>
  );
};

export default AudioController;
