import React, { useState } from "react";
import { db } from "./Database/firebaseConfig";
import { doc, getDoc, } from "firebase/firestore";
import { signInWithEmailAndPassword, signOut } from "firebase/auth";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "./firebaseConfig";

const Login = ({ onLogin }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [mostrarPassword, setMostrarPassword] = useState(false);

    const obtenerRutaPersonalizada = async (email) => {
    const idSeguro = email.replace(/[@.]/g, "_");
    const ref = doc(db, "clientes", idSeguro);
    const snap = await getDoc(ref);

    const registrarCliente = async (email, categoria, nombreCliente) => {
    const idSeguro = email.replace(/[@.]/g, "_");  // üìå Sanitiza el ID
    await setDoc(doc(db, "clientes", idSeguro), {
        categoria,
        nombreCliente,
        rutaPersonalizada: `/${categoria}/${nombreCliente}/invitaciones`
    });
    };

    if (snap.exists()) {
        return snap.data().rutaPersonalizada;
    } else {
        throw new Error("Cliente no registrado en Firestore.");
    }
  };

  const handleLogin = async () => {
    try {
        const userCred = await signInWithEmailAndPassword(auth, email, password);
        const userEmail = userCred.user.email;
        const clienteConfig = rutasClientes[userEmail];

        if (!clienteConfig) {
        alert("‚ùå No se encontr√≥ configuraci√≥n para este cliente.");
        return;
        }

        const rutaPersonalizada = `/${clienteConfig.categoria}/${clienteConfig.nombreCliente}/invitaciones`;
        onLogin(userCred.user);
        window.location.href = rutaPersonalizada;  // ‚úÖ Redirecci√≥n autom√°tica
    } catch (err) {
        alert("‚ùå Error al iniciar sesi√≥n: " + err.message);
    }
    };


  const handleLogout = () => {
    signOut(auth).then(() => {
      alert("üëã Sesi√≥n cerrada");
      onLogin(null);
    });
  };

  const handleRegister = async () => {
    try {
        const userCred = await createUserWithEmailAndPassword(auth, email, password);
        onLogin(userCred.user);  // ‚úÖ Inicia sesi√≥n autom√°ticamente despu√©s de registrarse
        alert("üéâ Registro exitoso");
    } catch (err) {
        alert("‚ùå Error al registrarse: " + err.message);
    }
  };


  return (
    <div>
      <input
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Correo"
      />
      <input
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Contrase√±a"
        type={mostrarPassword ? "text" : "password"}
      />
      <button onClick={() => setMostrarPassword((prev) => !prev)}>
        {mostrarPassword ? "üôà Ocultar" : "üëÅÔ∏è Ver"}
      </button>
      <button onClick={handleLogin}>Iniciar sesi√≥n</button>
      <button onClick={handleRegister}>Registrarse</button>
    </div>
  );
};

export default Login;
