import React, { useState } from "react";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { setDoc, doc } from "firebase/firestore";
import { auth, db } from "./firebaseConfig";

const RegistroCliente = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nombreCliente, setNombreCliente] = useState("");
  const [tipo_categoria, settipo_categoria] = useState("XV A√±os"); // Puedes hacer esto din√°mico

  const registrarCliente = async (email, tipo_tipo_categoria, nombreCliente) => {
    const correoSeguro = email.replace(/[@.]/g, "_");
    await setDoc(doc(db, "clientes", correoSeguro), {
      nombreCliente,
      tipo_categoria,
      rutaPersonalizada: `/${tipo_categoria}/${nombreCliente}/invitaciones`,
    });
  };

  const handleRegister = async () => {
    if (!email || !password || !nombreCliente || !tipo_categoria) {
      alert("‚ö†Ô∏è Por favor, completa todos los campos.");
      return;
    }

    try {
      const userCred = await createUserWithEmailAndPassword(auth, email, password);
      await registrarCliente(email, tipo_categoria, nombreCliente);
      alert("‚úÖ Registro exitoso");
      onLogin(userCred.user);
    } catch (err) {
      alert("‚ùå Error al registrarse: " + err.message);
    }
  };

  return (
    <div>
      <h2>üìù Registro de Cliente</h2>
      <input
        value={nombreCliente}
        onChange={(e) => setNombreCliente(e.target.value)}
        placeholder="Nombre del cliente (ej. Samuel)"
      />
      <input
        value={tipo_categoria}
        onChange={(e) => settipo_categoria(e.target.value)}
        placeholder="Categor√≠a del evento (ej. XV A√±os)"
      />
      <input
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Correo"
      />
      <input
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Contrase√±a"
        type="password"
      />
      <button onClick={handleRegister}>Registrarse</button>
    </div>
  );
};

export default RegistroCliente;
