import React, { useState, useEffect } from "react";
import { gsap } from "gsap";
import "./index.css";

function Invitacion3() {
  const [sobreAbierto, setSobreAbierto] = useState(false);

  useEffect(() => {
    if (sobreAbierto) abrirSobre();
  }, [sobreAbierto]);

  const abrirSobre = () => {
    gsap.to(".sello", { duration: 1, opacity: 0, ease: "power2.out" });

    gsap.to("#sobre_hoja1", {
      duration: 3,
      transform: "translate3d(0px, -100%, 0px)", // ðŸ”¥ Movimiento suave hacia arriba
      ease: "power2.inOut"
    });

    gsap.to("#sobre_hoja2", {
      duration: 3.5,
      transform: "translate3d(0px, 100%, 0px)", // ðŸ”¥ Movimiento suave hacia abajo
      ease: "power2.inOut",
      onComplete: () => {
        const sobreElement = document.querySelector(".sobre");
        if (sobreElement) sobreElement.remove();
      }
    });
  };

  return (
    <>
      {!sobreAbierto && (
        <div className="sobre">
          <div className="first" id="sobre_hoja1"></div>
          <button className="sello" onClick={() => setSobreAbierto(true)}>Abrir InvitaciÃ³n</button>
          <div className="second" id="sobre_hoja2"></div>
        </div>
      )}

      {sobreAbierto && (
        <div className="contenido">
          <h1>Â¡Bienvenido a la invitaciÃ³n!</h1>
        </div>
      )}
    </>
  );
}

export default prueba;
