import React, { useRef, useState, useEffect } from "react";
import imagenes from "../assets/imagenes"; // Asegúrate de tener soundOnIcon y muteIcon
import "../invit-deluxe/Invit2.css";

const AudioController = () => {
  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [icon, setIcon] = useState(imagenes.muteIcon); // inicia en silencio

  const toggleMusic = () => {
    const audio = audioRef.current;
    if (!audio) return;

    if (isPlaying) {
      audio.pause();
      setIcon(imagenes.muteIcon);
    } else {
      audio.play().catch((err) => {
        if (err.name !== "AbortError") {
          console.error("Error al reproducir el audio:", err);
        }
      });
      setIcon(imagenes.soundOnIcon);
    }
    setIsPlaying(!isPlaying);
  };

  useEffect(() => {
    const handlePlayRequest = () => {
      const audio = audioRef.current;
      if (!audio) return;

      audio.play().then(() => {
        setIsPlaying(true);
        setIcon(imagenes.soundOnIcon);
      }).catch((err) => {
        console.warn("No se pudo reproducir el audio desde evento externo:", err);
      });
    };

    window.addEventListener("reproducir-audio", handlePlayRequest);

    return () => {
      window.removeEventListener("reproducir-audio", handlePlayRequest);
    };
  }, []);


  return (
    <section id="fixed-items">
      <button id="toggleButton" onClick={toggleMusic}>
        <img
          id="soundIcon"
          src={icon}
          alt="Sound Icon"
          className="print"
        />
      </button>
      <audio ref={audioRef} id="music" loop>
        <source src="/assets/joansebastian-masalladeelsol.mp3" type="audio/mpeg" />
        Tu navegador no soporta la reproducción de audio.
      </audio>
    </section>
  );
};

export default AudioController;
