import React, { useState } from "react";
import "../Database/firebaseConfig"; // Ajusta segÃºn la ubicaciÃ³n correcta
import FotoSection from "../componentes/FotoSection"; // ðŸ“Œ Debe estar en `componentes/`
import "../assets/imagenes"; // ðŸ“Œ Ajusta segÃºn la ubicaciÃ³n correcta
import "../invit.css"; // ðŸ“Œ Ajusta segÃºn la ubicaciÃ³n correcta


const App = () => {
  const [nombre, setNombre] = useState("Invitado");
  const [invitadosAdultos, setInvitadosAdultos] = useState(1);
  const [invitadosNiÃ±os, setInvitadosNiÃ±os] = useState(0);
  const [incluirNiÃ±os, setIncluirNiÃ±os] = useState(false);
  const [totalInvitados, setTotalInvitados] = useState(1); // ðŸ†• Nuevo estado para reflejar la suma
  const [linkGenerado, setLinkGenerado] = useState("");
  const [linkAcortado, setLinkAcortado] = useState("");

  // ðŸ†• FunciÃ³n para generar un ID autoincrementable
  const generarIDInvitacion = async (tipoDiseÃ±o, nombreCliente) => {
    const querySnapshot = await getDocs(collection(db, "invitaciones"));
    const cantidadUsada = querySnapshot.docs.filter(doc => doc.data().tipoDiseÃ±o === tipoDiseÃ±o).length + 1;
    return `${tipoDiseÃ±o}${cantidadUsada}${nombreCliente.replace(/\s+/g, "")}`;
  };

  // ðŸ“Œ Se actualiza `totalInvitados` dinÃ¡micamente cuando cambian adultos o niÃ±os
  useEffect(() => {
    setTotalInvitados(invitadosAdultos + (incluirNiÃ±os ? invitadosNiÃ±os : 0));
  }, [invitadosAdultos, invitadosNiÃ±os, incluirNiÃ±os]);

  // ðŸ“Œ FunciÃ³n para actualizar datos y guardar en Firebase
  const actualizarDatos = async (nuevoNombre, nuevaCantidadAdultos, nuevaCantidadNiÃ±os, incluirNiÃ±os) => {
    let nombreLimpio = nuevoNombre.replace(/\s+/g, " ").trim();
    let nombreFinal = nombreLimpio.length > 11 ? nombreLimpio + ",\n" : nombreLimpio;
    const idInvitacion = await generarIDInvitacion("RJ", nuevoNombre);
    const nuevoLink = `https://tuweb.com/invitaciones/${idInvitacion}`;

    setNombre(nombreFinal);
    setInvitadosAdultos(nuevaCantidadAdultos);
    setInvitadosNiÃ±os(incluirNiÃ±os ? nuevaCantidadNiÃ±os : 0);
    setLinkGenerado(nuevoLink);

    try {
      await addDoc(collection(db, "invitaciones"), {
        id: idInvitacion,
        nombre: nombreFinal,
        invitadosAdultos: nuevaCantidadAdultos,
        invitadosNiÃ±os: incluirNiÃ±os ? nuevaCantidadNiÃ±os : 0,
        totalInvitados: nuevaCantidadAdultos + (incluirNiÃ±os ? nuevaCantidadNiÃ±os : 0), // âœ… Se almacena correctamente en Firebase
        link: nuevoLink,
        timestamp: new Date(),
      });

      // ðŸ“Œ Acortar el enlace con TinyURL
      const response = await axios.get(`https://tinyurl.com/api-create.php?url=${nuevoLink}`);
      setLinkAcortado(response.data);
    } catch (error) {
      console.error("Error al guardar la invitaciÃ³n:", error);
    }
  };

  // ðŸ“Œ FunciÃ³n para copiar el enlace
  const copiarAlPortapapeles = () => {
    navigator.clipboard.writeText(linkAcortado || linkGenerado);
    alert("Enlace copiado al portapapeles.");
  };

  return (
    <div>
      <FormGen actualizarDatos={actualizarDatos} />

      {linkGenerado && (
        <div>
          <p>
            Tu invitaciÃ³n ha sido generada:{" "}
            <a href={linkAcortado || linkGenerado} target="_blank" rel="noopener noreferrer">
              {linkAcortado || linkGenerado}
            </a>
          </p>
          <button onClick={copiarAlPortapapeles}>ðŸ“‹ Copiar enlace</button>
        </div>
      )}

      <FotoSection
        imageSrc={imagenes.img9}
        buttonText1="Confirmar asistencia"
        buttonLink1="https://api.whatsapp.com/send?phone=+525619060019&text=Gracias por invitarme!"
        buttonText2="No puedo asistir"
        buttonLink2="https://api.whatsapp.com/send?phone=+525619060019&text=No podre asistir."
        invitacion={{
          nombre: `${nombre} estÃ¡s invitado a mi Fiesta`,
          invitados: `InvitaciÃ³n para ${totalInvitados} personas`, // âœ… Ahora se actualiza dinÃ¡micamente
        }}
      />
    </div>
  );
};

export default App;
