import React, { useState } from "react";
import "./FormGen.css";

const FormGen = ({ actualizarDatos }) => {
  if (!actualizarDatos) {
    console.error("Error: La función `actualizarDatos` no fue pasada a `Invitacion`.");
  }

  const [nombre, setNombre] = useState("");
  const [invitados, setInvitados] = useState(1);

  // Limpieza del nombre para eliminar espacios extra y mantener un solo renglón
  const manejarCambioNombre = (e) => {
    let valor = e.target.value.trim().replace(/\s+/g, " ");
    setNombre(valor);
  };

  // Solo aceptar números en la entrada de invitados
  const manejarCambioInvitados = (e) => {
    let valor = parseInt(e.target.value);
    if (!isNaN(valor) && valor >= 1) {
      setInvitados(valor);
    }
  };

  const manejarEnvio = (e) => {
    e.preventDefault();
    actualizarDatos(nombre, invitados);
  };

  return (
    <section id="asistencia">
      <div className="card">
        <h1>Genera tus Invitaciones</h1>
        <h2>Ingresa el número de boletos por invitación así como el nombre de la Familia y/o invitado</h2>
        <form onSubmit={manejarEnvio}>
          <label>Nombre de la Familia:</label>
          <textarea 
            value={nombre} 
            onChange={manejarCambioNombre} 
            required 
            rows={nombre.length > 15 ? 2 : 1} // Expande si el nombre supera los 15 caracteres
          />

          <label>Número de Invitados:</label>
          <input 
            type="number" 
            value={invitados} 
            min="1" 
            onChange={manejarCambioInvitados} 
            required 
          />

          <button type="submit">Generar Invitación</button>
        </form>
      </div>
    </section>
  );
};

export default FormGen;
