import React, { useState } from "react";
import { db } from "./firebaseConfig";

import imagenes from "../assets/imagenes";
import { collection, doc, getDocs, setDoc } from "firebase/firestore";
import { signInWithEmailAndPassword, signOut, createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "./firebaseConfig";
import "./Login.css"

const Login = ({ onLogin, setVerificandoSesion, setUsuarioActual }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mostrarPassword, setMostrarPassword] = useState(false);
  

  // âœ… Buscar ruta personalizada en Firestore
  const obtenerRutaPersonalizada = async (email) => {
    const categorias = ["XV AÃ±os", "Eventos Empresariales", "Bodas"]; // o hazlo dinÃ¡mico

    for (const categoria of categorias) {
        const clientesRef = collection(db, "Clientes", "Categorias", categoria);
        const snapshot = await getDocs(clientesRef);

        for (const docSnap of snapshot.docs) {
        const data = docSnap.data();
        if (data.correo === email) {
            return data.rutaPersonalizada;
        }
        }
    }

    throw new Error("Cliente no registrado en Firestore.");
  };

  const handleLogin = async () => {
  try {
    setVerificandoSesion(true); // âœ… Activa el estado de carga

    const userCred = await signInWithEmailAndPassword(auth, email, password);
    const user = userCred.user;
    const ruta = await obtenerRutaPersonalizada(user.email);

    setUsuarioActual(user);       // âœ… Actualiza el estado
    onLogin(user);                // âœ… Notifica al padre si es necesario
    setVerificandoSesion(false); // âœ… Apaga la pantalla de carga

    window.location.href = ruta; // ðŸ”€ Redirige al usuario
  } catch (err) {
    alert("âŒ El correo o contraseÃ±a son incorrectos: ");
    setVerificandoSesion(false); // âŒ Evita que se quede en modo cargando
  }
};


  const handleLogout = () => {
    signOut(auth).then(() => {
      alert("ðŸ‘‹ SesiÃ³n cerrada");
      onLogin(null);
    });
  };

  return (
  <div className="fondo-login">
    <div className="wrapper">
      <form onSubmit={(e) => {
          e.preventDefault();
          handleLogin();
        }}>
        <h2>Iniciar sesiÃ³n</h2>

        <div className="input-field">
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <label>Correo</label>
        </div>

        {/* Campo de contraseÃ±a con Ã­cono en esquina */}
        <div className="input-field password-field">
          <input
            type={mostrarPassword ? "text" : "password"}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <label>ContraseÃ±a</label>
          <span
            className="toggle-password"
            onClick={() => setMostrarPassword((prev) => !prev)}
          >
            <i className={`fa-solid ${mostrarPassword ? "fa-eye" : "fa-eye-slash"}`}></i>
          </span>
        </div>

        <div className="forget">
          <a href="#">Â¿Olvidaste tu contraseÃ±a?</a>
        </div>

        <button className="login" type="submit" onClick={handleLogin}>
          Iniciar sesiÃ³n
        </button>
      </form>
    </div>
  </div>
  );
};

export default Login;
