import React, { useState } from "react";
import "./FormGen.css";

const FormGen = ({ actualizarDatos }) => {
  if (!actualizarDatos) {
    console.error("Error: La funciÃ³n `actualizarDatos` no fue pasada correctamente.");
  }

  const [nombre, setNombre] = useState("");
  const [invitadosAdultos, setInvitadosAdultos] = useState(1);
  const [invitadosNiÃ±os, setInvitadosNiÃ±os] = useState(0);
  const [incluirNiÃ±os, setIncluirNiÃ±os] = useState(false);

  const manejarCambioNombre = (e) => {
    let valor = e.target.value.replace(/\s{2,}/g, " "); 

    if (!/^[a-zA-ZÃ¡Ã©Ã­Ã³ÃºÃÃ‰ÃÃ“ÃšÃ±Ã‘\s.,\-\/]*$/.test(valor)) {
      alert("Solo puedes introducir texto y los caracteres especiales: . , - /");
      return;
    }

    if (valor.length > 22) {
      alert("El nombre no puede tener mÃ¡s de 22 caracteres.");
      return;
    }

    setNombre(valor);
  };
  

  const manejarCambioInvitadosAdultos = (e) => {
    let valor = e.target.value;

    if (valor === "") {
      setInvitadosAdultos("");
      return;
    }

    let numeroInvitados = parseInt(valor, 10);
    if (isNaN(numeroInvitados) || numeroInvitados < 1 || numeroInvitados > 25) {
      alert("El nÃºmero de invitados adultos debe estar entre 1 y 25.");
      return;
    }

    setInvitadosAdultos(numeroInvitados);
  };

  // âœ… CorrecciÃ³n: Definir `manejarCambioInvitNiÃ±os`
  const manejarCambioInvitadosNiÃ±os = (e) => {
    let valor = e.target.value;
  
    if (valor === "") {
      return; // âœ… Permite borrar sin afectar el estado
    }
  
    let numeroInvitados = parseInt(valor, 10);
    if (isNaN(numeroInvitados) || numeroInvitados < 1 || numeroInvitados > 25) {
      alert("El nÃºmero de invitados niÃ±os debe estar entre 0 y 25.");
      return;
    }
  
    setInvitadosNiÃ±os(numeroInvitados);
  };
  
  

  const manejarCheckbox = () => {
    setIncluirNiÃ±os(!incluirNiÃ±os);
    
    if (!incluirNiÃ±os) {
      setInvitadosNiÃ±os(prev => prev > 0 ? prev : 1); // âœ… Solo asigna un valor si el usuario no ingresÃ³ uno
    }
  };
  

  const manejarEnvio = (e) => {
    e.preventDefault();
    const totalInvitados = invitadosAdultos + (incluirNiÃ±os ? invitadosNiÃ±os : 0);
    actualizarDatos(nombre, invitadosAdultos, incluirNiÃ±os ? invitadosNiÃ±os : 0, incluirNiÃ±os, totalInvitados);
  };

  const FormGen = ({ actualizarDatos, handleCompartir, linkGenerado }) => {
    if (!actualizarDatos || !handleCompartir || linkGenerado === undefined) {
      console.error("Error: Algunas funciones o valores no fueron pasados correctamente.");
    }
  };
  

  return (
    <section id="asistencia">
      <div className="card">
        <h1>Genera tu InvitaciÃ³n</h1>
        <form onSubmit={manejarEnvio}>
          <label>Nombre del Invitado:</label>
          <input type="text" value={nombre} onChange={manejarCambioNombre} required />

          <label>NÃºmero de Invitados (Adultos):</label>
          <input type="number" value={invitadosAdultos} min="1" max="25" onChange={manejarCambioInvitadosAdultos} required />

          <label>
            <input type="checkbox" checked={incluirNiÃ±os} onChange={manejarCheckbox} /> Â¿Incluir niÃ±os?
          </label>

          {incluirNiÃ±os && (
            <>
              <label>NÃºmero de Invitados (NiÃ±os):</label>
              <input type="number" defaultValue={invitadosNiÃ±os !== null ? invitadosNiÃ±os : ""} min="1" max="25" onChange={manejarCambioInvitadosNiÃ±os} required />
            </>
          )}

          <button type="submit">Generar InvitaciÃ³n</button>

          {/* ðŸ“Œ Solo muestra el botÃ³n si `linkGenerado` tiene un valor */}
          {linkGenerado && (
            <button type="button" onClick={handleCompartir}>ðŸ“¤ Compartir InvitaciÃ³n</button>
          )}
        </form>
      </div>
    </section>
  );
};

export default FormGen;
