import React, { useState } from "react";
import { login, register, logout, changeUserPassword } from "./firebaseAuth";


const Login = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mostrarPassword, setMostrarPassword] = useState(false);

  const handleLogin = async () => {
    try {
      const user = await login(email, password);
      console.log("‚úÖ Usuario logueado:", user.uid);
      onLogin(user);
    } catch (error) {
      alert("‚ùå Error al iniciar sesi√≥n: " + error.message);
    }
  };

  const handleRegister = async () => {
    try {
      const user = await register(email, password);
      console.log("‚úÖ Usuario registrado:", user.uid);
      onLogin(user);
    } catch (error) {
      alert("‚ùå Error al registrar: " + error.message);
    }
  };

  return (
    <div className="login-container">
      <h2>üîê Login por Email</h2>

      <input
        type="email"
        placeholder="Correo"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />

      <div style={{ display: "flex", alignItems: "center" }}>
        <input
          type={mostrarPassword ? "text" : "password"}
          placeholder="Contrase√±a"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={() => setMostrarPassword(!mostrarPassword)}>
          {mostrarPassword ? "üôà" : "üëÅ"}
        </button>
      </div>

      <button onClick={handleLogin}>Iniciar sesi√≥n</button>
      <button onClick={handleRegister}>Registrarse</button>
    </div>
  );
};

// üîÅ Funciones auxiliares para App.js
export const handleLogout = async () => {
  await logout();
  window.location.reload();
};

export const handleCambiarPassword = async (nuevoPassword, setNuevoPassword, setMostrarMenu) => {
  try {
    await changeUserPassword(nuevoPassword);
    alert("‚úÖ Contrase√±a actualizada correctamente");
    setNuevoPassword("");
    setMostrarMenu(false);
  } catch (error) {
    alert("‚ùå Error al cambiar la contrase√±a: " + error.message);
  }
};

export default Login;
