import React, { useState } from "react";
import "./FormGen.css";

const FormGen = ({ actualizarDatos }) => {
  if (!actualizarDatos) {
    console.error("Error: La función `actualizarDatos` no fue pasada correctamente.");
  }

  const [nombre, setNombre] = useState("");
  const [invitAdultos, setInvitAdultos] = useState(1);
  const [invitNiños, setInvitNiños] = useState(0);
  const [includNiños, setIncludNiños] = useState(false);

  const manejarCambioNombre = (e) => {
    let valor = e.target.value.replace(/\s{2,}/g, " "); 

    if (!/^[a-zA-ZáéíóúÁÉÍÓÚñÑ\s.,\-\/]*$/.test(valor)) {
      alert("Solo puedes introducir texto y los caracteres especiales: . , - /");
      return;
    }

    if (valor.length > 22) {
      alert("El nombre no puede tener más de 22 caracteres.");
      return;
    }

    setNombre(valor);
  };

  // 🆕 Definir `manejarCambioInvitAdultos` antes de usarlo
  const manejarCambioInvitAdultos = (e) => {
    let valor = e.target.value;

    if (valor === "") {
      setInvitAdultos("");
      return;
    }

    let numeroInvitados = parseInt(valor, 10);
    if (isNaN(numeroInvitados) || numeroInvitados < 1 || numeroInvitados > 25) {
      alert("El número de invitados adultos debe estar entre 1 y 25.");
      return;
    }

    setInvitAdultos(numeroInvitados);
  };

  // 🆕 Definir `manejarCambioInvitNiños` antes de usarlo
  const manejarCambioInvitNiños = (e) => {
    let valor = e.target.value;

    if (valor === "") {
      setInvitNiños("");
      return;
    }

    let numeroInvitados = parseInt(valor, 10);
    if (isNaN(numeroInvitados) || numeroInvitados < 0 || numeroInvitados > 25) {
      alert("El número de invitados niños debe estar entre 0 y 25.");
      return;
    }

    setInvitNiños(numeroInvitados);
  };

  const manejarCheckbox = () => {
    setIncludNiños(!includNiños);
    if (!includNiños) {
      setInvitNiños(1);
    } else {
      setInvitNiños(0);
    }
  };

  const manejarEnvio = (e) => {
    e.preventDefault();
    actualizarDatos(nombre, invitAdultos, includNiños ? invitNiños : 0, includNiños);
  };

  return (
    <section id="asistencia">
      <div className="card">
        <h1>Genera tu Invitación</h1>
        <form onSubmit={manejarEnvio}>
          <label>Nombre del Invitado:</label>
          <input type="text" value={nombre} onChange={manejarCambioNombre} required />

          <label>Número de Invitados (Adultos):</label>
          <input type="number" value={invitAdultos} min="1" max="25" onChange={manejarCambioInvitAdultos} required />

          <label>
            <input type="checkbox" checked={includNiños} onChange={manejarCheckbox} /> ¿Incluir niños?
          </label>

          {includNiños && (
            <>
              <label>Número de Invitados (Niños):</label>
              <input type="number" value={invitNiños} min="0" max="25" onChange={manejarCambioInvitNiños} required />
            </>
          )}

          <button type="submit">Generar Invitación</button>
        </form>
      </div>
    </section>
  );
};

export default FormGen;
