import React, { useEffect, useRef, useState } from "react";

function AnimacionScroll({ className, children }) {
  const ref = useRef(null);
  const [activeClass, setActiveClass] = useState(""); // ðŸ”¥ Clase de animaciÃ³n dinÃ¡mica

  useEffect(() => {
    const handleScroll = () => {
      if (!ref.current) return;

      const windowHeight = window.innerHeight;
      const showTop = ref.current.getBoundingClientRect().top;
      const showPoint = 100;
      
      if (showTop < windowHeight - showPoint) {
        setActiveClass(getAnimationClass(className)); // âœ… Activa animaciÃ³n
      } else {
        setActiveClass(""); // âœ… Remueve animaciÃ³n si no estÃ¡ en pantalla
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll); // ðŸ”¥ Limpia el evento al desmontar
  }, [className]); // ðŸ”¥ Se ejecuta cuando cambia la clase

  return (
    <div ref={ref} className={`${className} ${activeClass}`}>
      {children}
    </div>
  );
}

// ðŸ”¥ FunciÃ³n para asignar las clases de animaciÃ³n especÃ­ficas
function getAnimationClass(className) {
  if (className.includes("show-n-x")) return "active-n-x";
  if (className.includes("show-p-x")) return "active-p-x";
  if (className.includes("show-n-y")) return "active-n-y";
  if (className.includes("show-p-y")) return "active-p-y";
  if (className.includes("show")) return "active";
  return "";
}

export default AnimacionScroll;
