import React, { useState, useEffect, useCallback } from "react";
import { db } from "./Database/firebaseConfig";
import { collection, doc, setDoc, getDocs } from "firebase/firestore";
import axios from "axios";
import FotoSection from "./FotoSection";
import FormGen from "./componentes/FormGen";
import Invitacion from "./Invitacion";
import Invitacion3 from "./Invitacion3";
import imagenes from "./assets/imagenes";
import "./invit.css";
import SendInvitacion from "./Database/SendInvitacion";
import MostrarInvitaciones from "./Database/MostrarInvitaciones";
import RegistroCliente from "./Database/RegistroCliente";  // ajusta la ruta seg√∫n d√≥nde lo guardaste


import Login from "./Database/Login";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "./Database/firebaseConfig";
import { signOut } from "firebase/auth";

const generarID = (nombreOriginal) => {
  return nombreOriginal
    .normalize("NFD")                   // separa los acentos
    .replace(/[\u0300-\u036f]/g, "")   // elimina los acentos
    .replace(/\s+/g, "")               // elimina espacios
    .toLowerCase();                    // convierte a min√∫sculas
};


const App = () => {
  const [nombre, setNombre] = useState(() => {
    return localStorage.getItem("nombreInvitado") || "Invitado";
  });
  const [invitadosAdultos, setInvitadosAdultos] = useState(1);
  const [invitadosNi√±os, setInvitadosNi√±os] = useState(0);
  const [incluirNi√±os, setIncluirNi√±os] = useState(false);
  const [totalInvitados, setTotalInvitados] = useState(1);
  const [linkGenerado, setLinkGenerado] = useState("");
  const [linkAcortado, setLinkAcortado] = useState("");
  const [mostrarMensaje, setMostrarMensaje] = useState(false);
  const [listaInvitaciones, setListaInvitaciones] = useState([]);
  const [nombreCliente, setNombreCliente] = useState("Invitado");
  const [categoriaEvento, setCategoriaEvento] = useState("XV A√±os");  // valor default temporal
  const [linkPrevisualizacion, setLinkPrevisualizacion] = useState("");
  const [usuarioActual, setUsuarioActual] = useState(null);


  const handleLogout = () => {
  signOut(auth).then(() => {
    alert("üëã Sesi√≥n cerrada");
    setUsuarioActual(null);
  });
};


  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setUsuarioActual(user);
    });
    return () => unsubscribe();
  }, []);



  useEffect(() => {
    if (linkGenerado) {
      setMostrarMensaje(true);

      // üìå Ocultar el mensaje despu√©s de 5 segundos
      const timer = setTimeout(() => {
        setMostrarMensaje(false);
      }, 5000);

      return () => clearTimeout(timer);
    }
  }, [linkGenerado]);

  useEffect(() => {
    const manejarScroll = () => {
      setMostrarMensaje(false); // üìå Ocultar cuando el usuario hace scroll
    };

    window.addEventListener("scroll", manejarScroll);
    return () => window.removeEventListener("scroll", manejarScroll);
  }, []);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const cliente = urlParams.get("cliente") || "Invitado";  // ‚úÖ Captura el cliente de la URL
    setNombreCliente(cliente);
  }, []);

  useEffect(() => {
  const obtenerDatosCliente = async () => {
    if (!usuarioActual?.email) return;

    try {
      const categoriasPosibles = ["XV A√±os", "Eventos Empresariales", "Bodas"]; // ajusta seg√∫n tu sistema

      for (const categoria of categoriasPosibles) {
        const clientesRef = collection(db, "Clientes", "Categorias", categoria);
        const snapshot = await getDocs(clientesRef);

        for (const clienteDoc of snapshot.docs) {
          const data = clienteDoc.data();
          if (data.correo === usuarioActual.email) {
            setNombreCliente(clienteDoc.id);       // üëà Ahora s√≠ es ‚ÄúBriseyda‚Äù, ‚ÄúSamuel‚Äù, etc.
            setCategoriaEvento(categoria);         // üëà ‚ÄúXV A√±os‚Äù, etc.
            return;
          }
        }
      }

      console.warn("‚ùå Cliente no encontrado en la nueva estructura.");
    } catch (error) {
      console.error("‚ùå Error al consultar cliente:", error.message);
    }
  };

  obtenerDatosCliente();
}, [usuarioActual]);

  const handleCompartir = () => {
    if (!linkGenerado) {
      alert("Primero genera la invitaci√≥n antes de compartirla.");
      return;
    }

    // üìå Copia el enlace al portapapeles
    navigator.clipboard.writeText(linkGenerado);

    // üìå Enviar el enlace por WhatsApp
    const mensajeWhatsApp = `¬°Hola! Te comparto la invitaci√≥n: ${linkGenerado}`;
    const urlWhatsApp = `https://api.whatsapp.com/send?phone=+5619060019&text=${encodeURIComponent(mensajeWhatsApp)}`;

    window.open(urlWhatsApp, "_blank");
  };

  const copiarAlPortapapeles = () => {
    navigator.clipboard.writeText(linkAcortado || linkGenerado);
    alert("Enlace copiado al portapapeles.");
  };

  return (
    <div className="pagina">
      {!usuarioActual ? (
      <>
        <h2>üîí Inicia sesi√≥n</h2>
        <Login onLogin={(user) => setUsuarioActual(user)} />
        <RegistroCliente onLogin={(user) => setUsuarioActual(user)} />
      </>
    ) : (
      <>
      {/* üìå Mostrar el mensaje en un encabezado solo si `mostrarMensaje` est√° activado */}
      {mostrarMensaje && (
        <header className="invitacion-creada">
          <h1>üéâ Tu invitaci√≥n ha sido creada üéâ</h1>
        </header>
      )}

      <div className="cerrar-sesion">
          <p>Hola, {usuarioActual.email}</p>
          <button onClick={handleLogout}>Cerrar sesi√≥n</button>
      </div>

      <FormGen 
        actualizarDatos={actualizarDatos} 
        handleCompartir={handleCompartir} 
        linkGenerado={linkGenerado}
      />

      <MostrarInvitaciones
        CATEGORIA_EVENTO={CATEGORIA_EVENTO}
        NOMBRE_CLIENTE={NOMBRE_CLIENTE}
        setListaInvitaciones={setListaInvitaciones}
        setLinkPrevisualizacion={setLinkPrevisualizacion}
      />

      {linkPrevisualizacion && (
        <section className="contenedor-iframe">
          <h2 className="titulo-iframe">üîç Vista previa de las invitaciones</h2>
          <iframe
            src={linkPrevisualizacion}
            title="Previsualizaci√≥n de las invitaciones"
            className="iframe-preview"
            allow="fullscreen"
            loading="lazy"
            sandbox="allow-scripts allow-same-origin"
          />
        </section>
      )}
      </>
    )}
    </div>
  );
};

export default App;
