import React from "react";

const FotoSection = ({buttonText11, buttonLink11, buttonText22, buttonLink22, buttonText33, buttonLink33, buttonText44, buttonLink44, invitacion }) => {
  return (
    <section className="Confirmar">
      <div className="style">        
        {invitacion && (
          <div 
            className="contasis" 
            style={{ bottom: invitacion.nombre.length > 39 ? "21%" : "28%" }} // Ajuste dinámico
          >
            <h4 id="nombreInvitacion" style={{ whiteSpace: invitacion.nombre.length > 39 ? "pre-line" : "nowrap" }}>
              {invitacion.nombre}
            </h4>

            <h4 id="cantidadInvitados">{invitacion.invitados}</h4>
          </div>
        )}

        {/* Solo muestra el botón si hay texto y enlace */}
        {buttonText11 && buttonLink11 && (
          <button className="Confirmar1" onClick={() => window.open(buttonLink11, "_blank")}>
            {buttonText11}
          </button>
        )}

        {buttonText22 && buttonLink22 && (
          <button className="No-asistir1" onClick={() => window.open(buttonLink22, "_blank")}>
            {buttonText22}
          </button>
        )}

        {/* {buttonText33 && buttonLink33 && (
          <button className="Ubicacion" onClick={() => window.open(buttonLink33, "_blank")}>
            {buttonText33}
          </button>
        )}

        {buttonText44 && buttonLink44 && (
          <button className="blason" onClick={() => window.open(buttonLink44, "_blank")}>
            {buttonText44}
          </button>
        )} */}
      </div>
    </section>
  );
};

export default FotoSection;
