import React, { useState } from "react";
import { db } from "./firebaseConfig";

import imagenes from "../assets/imagenes";
import { collection, doc, getDocs, setDoc } from "firebase/firestore";
import { signInWithEmailAndPassword, signOut, createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "./firebaseConfig";
import "./Login.css"

const Login = ({ onLogin, setVerificandoSesion, setUsuarioActual }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mostrarPassword, setMostrarPassword] = useState(false);
  const [errorLogin, setErrorLogin] = useState(false);

  

  // ‚úÖ Buscar ruta personalizada en Firestore
  const obtenerRutaPersonalizada = async (email) => {
    const categorias = ["XV A√±os", "Eventos Empresariales", "Bodas"]; // o hazlo din√°mico

    for (const categoria of categorias) {
        const clientesRef = collection(db, "Clientes", "Categorias", categoria);
        const snapshot = await getDocs(clientesRef);

        for (const docSnap of snapshot.docs) {
        const data = docSnap.data();
        if (data.correo === email) {
            return data.rutaPersonalizada;
        }
        }
    }

    throw new Error("Cliente no registrado en Firestore.");
  };

  const handleLogin = async () => {
  try {
    setVerificandoSesion(true); // ‚úÖ Activa el estado de carga
    setErrorLogin(false);

    const userCred = await signInWithEmailAndPassword(auth, email, password);
    const user = userCred.user;
    const ruta = await obtenerRutaPersonalizada(user.email);

    setUsuarioActual(user);       // ‚úÖ Actualiza el estado
    onLogin(user);                // ‚úÖ Notifica al padre si es necesario
    setVerificandoSesion(false); // ‚úÖ Apaga la pantalla de carga

    window.location.href = ruta; // üîÄ Redirige al usuario
  } catch (err) {
      console.error("Error:", err.message);
      setErrorLogin(true); // üëà Muestra el mensaje de error
      setVerificandoSesion(false); 
    }
};


  const handleLogout = () => {
    signOut(auth).then(() => {
      alert("üëã Sesi√≥n cerrada");
      onLogin(null);
    });
  };

  return (
  <div className="fondo-login">
    <div className="wrapper">
      <form onSubmit={(e) => {
          e.preventDefault();
          handleLogin();
        }}>
        <h2>Iniciar sesi√≥n</h2>
        {errorLogin && (
          <div className="mensaje-error" data-testid="error-login">
            El correo o contrase√±a son incorrectos
          </div>
        )}



        <div className="input-field">
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <label>Correo</label>
        </div>

        {/* Campo de contrase√±a con √≠cono en esquina */}
        <div className="input-field password-field">
          <input
            type={mostrarPassword ? "text" : "password"}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <label>Contrase√±a</label>
          <span
            className="toggle-password"
            onClick={() => setMostrarPassword((prev) => !prev)}
          >
            <i className={`fa-solid ${mostrarPassword ? "fa-eye" : "fa-eye-slash"}`}></i>
          </span>
        </div>

        <div className="forget">
          <a href="#">¬øOlvidaste tu contrase√±a?</a>
        </div>

        <button className="login">
          Iniciar sesi√≥n
        </button>
      </form>
    </div>
  </div>
  );
};

export default Login;
