import React, { useRef, useState } from "react";
import imagenes from "../assets/imagenes"; // Asegúrate de que soundOnIcon y soundOffIcon están definidos allí

const AudioController = () => {
  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(true);

  const toggleMusic = () => {
    const audio = audioRef.current;
    if (!audio) return;

    if (audio.paused) {
      audio.play()
        .then(() => setIsPlaying(true))
        .catch((err) => {
          if (err.name !== "AbortError") {
            console.error("Error al reproducir:", err);
          }
        });
    } else {
      audio.pause();
      setIsPlaying(false);
    }
  };

  return (
    <section id="fixed-items">
      <button id="toggleButton" onClick={toggleMusic}>
        <img
          src={isPlaying ? imagenes.soundOnIcon : imagenes.soundOffIcon}
          alt="Ícono de sonido"
          className="print"
          style={{ width: "32px", height: "32px" }}
        />
      </button>

      {/* Ruta absoluta desde /public */}
      <audio ref={audioRef} autoPlay loop>
        <source src="/assets/joansebastian-masalladeelsol.mp3" type="audio/mpeg" />
        Tu navegador no soporta la reproducción de audio.
      </audio>
    </section>
  );
};

export default AudioController;
