import React, { useState } from "react";
import "./FormGen.css";

const FormGen = ({ actualizarDatos }) => {
  if (!actualizarDatos) {
    console.error("Error: La funci√≥n `actualizarDatos` no fue pasada correctamente.");
  }

  const [nombre, setNombre] = useState("");
  const [invitadosAdultos, setInvitadosAdultos] = useState(1);
  const [invitadosNi√±os, setInvitadosNi√±os] = useState(0);
  const [incluirNi√±os, setIncluirNi√±os] = useState(false);

  const manejarCambioNombre = (e) => {
    let valor = e.target.value.replace(/\s{2,}/g, " "); 

    if (!/^[a-zA-Z√°√©√≠√≥√∫√Å√â√ç√ì√ö√±√ë\s.,\-\/]*$/.test(valor)) {
      alert("Solo puedes introducir texto y los caracteres especiales: . , - /");
      return;
    }

    if (valor.length > 22) {
      alert("El nombre no puede tener m√°s de 22 caracteres.");
      return;
    }

    setNombre(valor);
  };

  const compartirInvitacion = async () => {
    if (!linkGenerado) {
      alert("Primero genera la invitaci√≥n antes de compartirla.");
      return;
    }
  
    try {
      await addDoc(collection(db, "invitaciones"), {
        nombre: nombre,
        invitadosAdultos: invitadosAdultos,
        invitadosNi√±os: incluirNi√±os ? invitadosNi√±os : 0,
        totalInvitados,
        link: linkGenerado,
        timestamp: new Date(),
      });
  
      navigator.clipboard.writeText(linkGenerado); // üìå Copia el enlace
  
      // üìå Abre WhatsApp con el enlace copiado
      const mensajeWhatsApp = `¬°Hola! Te comparto la invitaci√≥n: ${linkGenerado}`;
      window.open(`https://api.whatsapp.com/send?text=${encodeURIComponent(mensajeWhatsApp)}`, "_blank");
      
    } catch (error) {
      console.error("Error al compartir la invitaci√≥n:", error);
    }
  };
  

  const manejarCambioInvitadosAdultos = (e) => {
    let valor = e.target.value;

    if (valor === "") {
      setInvitadosAdultos("");
      return;
    }

    let numeroInvitados = parseInt(valor, 10);
    if (isNaN(numeroInvitados) || numeroInvitados < 1 || numeroInvitados > 25) {
      alert("El n√∫mero de invitados adultos debe estar entre 1 y 25.");
      return;
    }

    setInvitadosAdultos(numeroInvitados);
  };

  // ‚úÖ Correcci√≥n: Definir `manejarCambioInvitNi√±os`
  const manejarCambioInvitadosNi√±os = (e) => {
    let valor = e.target.value;
  
    if (valor === "") {
      return; // ‚úÖ Permite borrar sin afectar el estado
    }
  
    let numeroInvitados = parseInt(valor, 10);
    if (isNaN(numeroInvitados) || numeroInvitados < 1 || numeroInvitados > 25) {
      alert("El n√∫mero de invitados ni√±os debe estar entre 0 y 25.");
      return;
    }
  
    setInvitadosNi√±os(numeroInvitados);
  };
  
  

  const manejarCheckbox = () => {
    setIncluirNi√±os(!incluirNi√±os);
    
    if (!incluirNi√±os) {
      setInvitadosNi√±os(prev => prev > 0 ? prev : 1); // ‚úÖ Solo asigna un valor si el usuario no ingres√≥ uno
    }
  };
  

  const manejarEnvio = (e) => {
    e.preventDefault();
    const totalInvitados = invitadosAdultos + (incluirNi√±os ? invitadosNi√±os : 0);
    actualizarDatos(nombre, invitadosAdultos, incluirNi√±os ? invitadosNi√±os : 0, incluirNi√±os, totalInvitados);
  };

  return (
    <section id="asistencia">
      <div className="card">
        <h1>Genera tu Invitaci√≥n</h1>
        <form onSubmit={manejarEnvio}>
          <label>Nombre del Invitado:</label>
          <input type="text" value={nombre} onChange={manejarCambioNombre} required />

          <label>N√∫mero de Invitados (Adultos):</label>
          <input type="number" value={invitadosAdultos} min="1" max="25" onChange={manejarCambioInvitadosAdultos} required />

          <label>
            <input type="checkbox" checked={incluirNi√±os} onChange={manejarCheckbox} /> ¬øIncluir ni√±os?
          </label>

          {incluirNi√±os && (
            <>
              <label>N√∫mero de Invitados (Ni√±os):</label>
              <input type="number" defaultValue={invitadosNi√±os !== null ? invitadosNi√±os : ""} min="1" max="25" onChange={manejarCambioInvitadosNi√±os} required />
            </>
          )}

          <button type="submit">Generar Invitaci√≥n</button>
          <button type="button" onClick={manejarEnvio}>Compartir Invitaci√≥n</button>
        </form>
      </div>
    </section>
  );
};

