import React, { useState, useEffect, useCallback } from "react";
import { collection, doc, setDoc, addDoc } from "firebase/firestore";
import "./invit-deluxe/Invit2.css"
import imagenes from "./assets/imagenes";
import Galeria from "./invit-deluxe/Galeria";
import Itinerario from "./invit-deluxe/Itinerario";
import Countdown from "./invit-deluxe/Countdown";
import AnimacionScroll from "./invit-deluxe/AnimacionScroll";
import FotoSection from "./FotoSection";
import Confirmacion from "./invit-deluxe/Confirmacion";

const Separador = () => (
    <div className="icono-inv" data-icono="ornamento2"
      style={{
        marginTop: "3%",
        marginBottom: "-5%",
        width: "100%",
        maskImage: `url(${imagenes.ornamentos})`,
        WebkitMaskImage: `url(${imagenes.ornamentos})` // âœ… Para compatibilidad en Chrome/Safari
      }}
    ></div>
)

const Separador3 = () => (
    <div className="icono-inv" data-icono="ornamento3"
      style={{
        marginTop: "5%",
        marginBottom: "5%",
        width: "85%",
        maskImage: `url(${imagenes.ornamento3})`,
        WebkitMaskImage: `url(${imagenes.ornamento3})` // âœ… Para compatibilidad en Chrome/Safari
      }}
    ></div>
)

const SepTitle = () => (
    <div className="icono-title" data-icono="Separador2"
      style={{
        marginTop: "2%",
        marginBottom: "-5%",
        width: "100%",
        maskImage: `url(${imagenes.Separador2})`,
        WebkitMaskImage: `url(${imagenes.Separador2})` // âœ… Para compatibilidad en Chrome/Safari
      }}
    ></div>
)

const ImageSep = ( )=> (
<div className="imagensepa">
  <img className="decoracion imagen-v" src={imagenes.img11} alt="DecoraciÃ³n" />
</div>
)

const cuenta = "651651618681"; // NÃºmero de cuenta
const copiarCuenta = () => {
  navigator.clipboard.writeText(cuenta)
    .then(() => alert("NÃºmero de cuenta copiado ðŸ“‹"))
    .catch((err) => console.error("Error al copiar: ", err));
};

// Componente Ubicacion
const Ubicacion = ({ titulo, direccion, hora, imagen, link }) => (
  <a className="ubicacion" href={link} target="_blank" rel="noopener noreferrer">
    <div>
        <img className="foto aparecer" src={imagen} alt={`UbicaciÃ³n: ${titulo}`} />
      <p className="lugar">{titulo}</p>
      <span className="direccion">{direccion}</span>
      <span className="hora aparecer">{hora}</span>
      <div className="boton aparecer">Ir al Mapa</div>
    </div>
  </a>
);

function Invitacion3 ({ nombreInvitacion, totalInvitadosInvitacion }) {
  const [sobreAbierto, setSobreAbierto] = useState(false);

  return (
    <>
      {!sobreAbierto && (
        <div className="sobre">
          <div className="up" id="sobre_hoja1"></div>
          <button className="blason" onClick={() => setSobreAbierto(true)}>
            <Confirmacion
              invitacion={{
                nombre: (
                  <>
                    {nombreInvitacion}
                    <br />
                    <h4 className="portada">EstÃ¡s invitado</h4>
                  </>
                ),
              }}
            />
          </button>
          <div className="down" id="sobre_hoja2"></div>
        </div>
      )}

      {sobreAbierto && (
        <div className="contenido">
          {/* MARK: Decoraciones */}
          <div className="extremos">
            <div className="top">
              <img className="top-img" src={imagenes.flor1} alt="DecoraciÃ³n superior" />
            </div>
            <div className="bottom">
              <img className="bottom-img" src={imagenes.flor2} alt="DecoraciÃ³n inferior" />
            </div>
          </div>

          {/* MARK: Nombre */}
          <div className="encabezado">
            <h1 className="evento">XV</h1>
            <SepTitle />
            <h2 className="nombre-principal">Briseyda</h2>
          </div>

          {/* MARK: Foto */}
          <div className="foto aparecer">
            <img className="principal" src={imagenes.XV} alt="Foto de la festejada" />
            <img className="aro-foto" src={imagenes.AroFlor} alt="Aro decorativo" />
          </div>

          <div className="Contend">
            <img className="Fecha" src={imagenes.Fecha} alt="fecha" />
          </div>

          <h1 className="reloj">Â¡Cuenta regresiva para el evento!</h1>
          <Countdown day={0} month={"diciembre"} year={0} time={"0"} />
          <Separador3 />

          {/* MARK: Frases */}
          <AnimacionScroll className="frase show-p-y">
            <h1 className="invit">Estas invitado a mis<br />XV aÃ±os</h1>
            "Hoy quiero invitarte a un dÃ­a lleno de alegrÃ­a, mÃºsica y celebraciÃ³n.
            Mis XV aÃ±os son un momento que atesorarÃ© para siempre, y quiero vivirlo contigo
            y en compaÃ±Ã­a de mis familiares."
          </AnimacionScroll>

          <Separador3 />

          {/* MARK: Ubicaciones */}
          <AnimacionScroll className="iglesia show-p-x">
          <Ubicacion
            titulo="RectorÃ­a Nuestra SeÃ±ora Del Refugio"
            direccion="Av. Insurgentes Manzana 001, Capula, 54603 TepotzotlÃ¡n, MÃ©x."
            hora="3:45 PM"
            imagen={imagenes.iglesia}
            link="https://maps.app.goo.gl/vYWZwYKARzAEtoH59"
          />
          </AnimacionScroll>

          <AnimacionScroll className="recepcion show-n-x">
          <Ubicacion
            titulo="JardÃ­n Royal"
            direccion="C. Chilar Manzana 003, San Mateo Xoloc, 54602 TepotzotlÃ¡n, MÃ©x."
            hora="5:10 PM"
            imagen={imagenes.salon}
            link="https://maps.app.goo.gl/R9jzNjmfxAZnvKWF9"
          />
          </AnimacionScroll>

          <ImageSep />

          {/* MARK: Familia*/}
          <div className="familia">
            <AnimacionScroll className="Padres show-n-x">
              <h1 className="frase">Con la bendicion de Dios y en compaÃ±ia de:</h1>
              <h1 className="nombre">Mis Padres</h1>
              <Separador3 />
              <p className="parent texto">Angel Ortega Paredez<br />&<br />Jazmin Vazquez</p>
            </AnimacionScroll>
            <AnimacionScroll className="Padrinos show-p-x">
              <h1 className="nombre">Mis Padrinos</h1>
              <Separador3 />
              <p className="parent texto">Jose Miguel Monroy<br />&<br />Aracely Ortega Paredez</p>
            </AnimacionScroll>
          </div>

          {/* MARK: SPI */}
          <div className="imagensepa">
            <img className="decoracion imagen-v" src={imagenes.img12} alt="DecoraciÃ³n" />
          </div>

          {/* MARK: Separador*/}
          <Separador3 />

          <Galeria />
          <Separador3 />

          {/* MARK: SPI */}
          <div className="imagensepa">
            <img className="decoracion imagen-v" src={imagenes.img12} alt="DecoraciÃ³n" />
          </div>

          <h2 className="ith2">Programa del Evento</h2>
          <Separador3 />
          <Itinerario />
          <Separador3 />

          {/* MARK: SPI */}
          <div className="imagensepa">
            <img className="decoracion imagen-v" src={imagenes.img12} alt="DecoraciÃ³n" />
          </div>

          {/* MARK: Regalos */}
          <AnimacionScroll className="MesaRegalos show-n-y">
            <div className="banco">
              <h2 className="ith2">Mesa de Regalos</h2>
              <Separador3/>
              <h1>Datos Bancarios</h1>
              <div className="tarjetas" data-icono="tarjetas"
                style={{
                  marginTop: "5%",
                  marginBottom: "5%",
                  width: "85%",
                  maskImage: `url(${imagenes.tarjetas})`,
                  WebkitMaskImage: `url(${imagenes.tarjetas})` // âœ… Para compatibilidad en Chrome/Safari
                }}
              ></div>              
              <p>No es necesario estar cerca para hacer sentir el amor y cariÃ±o.
                AsÃ­ que si lo prefieres, puedes hacer una transferencia bancaria como regalo
                a la siguiente cuenta:
              </p>
              <h2>Cuenta BBVA:</h2>
              <h2 className="copy">{cuenta}</h2>
              <button className="boton-copiar" onClick={copiarCuenta}>Copiar</button>
              <h2>Beneficiaria:</h2>
              <h1>Briseyda Ortega Vazquez</h1>
            </div>
          </AnimacionScroll>
          <Separador3/>
          {/* MARK: Confirmacion */}
          
          <h2 className="ith2">Confirma tu Asistencia</h2>
          <div className="extra">
            <h1 className="informacion" >Favor de confirmar su asistencia antes del 10 de Diciembre</h1>
          </div>
          
          <Confirmacion
            buttonText11="Confirmar asistencia"
            buttonLink11="https://api.whatsapp.com/send?phone=+5619060019&text=Asistire"
            buttonText22="No puedo asistir"
            buttonLink22="https://api.whatsapp.com/send?phone=+5619060019&text=No%20Asistir%C3%A9%20"
            
          />
          {/* <Separador3/> */}
        </div>

      )}
    </>
  );
}

export default Invitacion3;
