import React, { useRef, useState } from "react";
import soundOnIcon from "../media/icons/sound_on.png";
import soundOffIcon from "../media/icons/sound_off.png";

const AudioController = () => {
  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(true);

  const toggleMusic = () => {
    const audio = audioRef.current;
    if (!audio) return;
    if (audio.paused) {
      audio.play();
      setIsPlaying(true);
    } else {
      audio.pause();
      setIsPlaying(false);
    }
  };

  return (
    <section id="fixed-items">
      <button id="toggleButton" onClick={toggleMusic}>
        <img
          src={isPlaying ? soundOnIcon : soundOffIcon}
          alt="Sound Icon"
          className="print"
        />
      </button>
      <audio ref={audioRef} autoPlay loop>
        <source src="../assets/Joan Sebastian - Mas alla de el sol ( Karaoke ).mp3" type="audio/mpeg" />
        Tu navegador no soporta la reproducci√≥n de audio.
      </audio>
    </section>
  );
};

export default AudioController;
