import React, { useState, useEffect } from "react";
import { collection, getDocs, deleteDoc, doc } from "firebase/firestore";
import { db } from "../Database/firebaseConfig";

// ✅ Función para obtener el cliente dinámicamente desde la URL
const obtenerClienteDesdeURL = () => {
  const urlParams = new URLSearchParams(window.location.search);
  return urlParams.get("cliente") || "Invitado";  // ✅ Captura el nombre del cliente desde la URL
};

const MostrarInvitaciones = ({ CATEGORIA_EVENTO, setListaInvitaciones }) => {
  const [invitaciones, setInvitaciones] = useState([]);
  const clienteID = obtenerClienteDesdeURL();  // ✅ Obtiene el cliente dinámicamente

  useEffect(() => {
    if (!CATEGORIA_EVENTO || !clienteID) {
      console.warn("⚠️ `CATEGORIA_EVENTO` o `clienteID` están vacíos. No se puede obtener invitaciones.");
      return;
    }

    const obtenerInvitaciones = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, CATEGORIA_EVENTO, clienteID, "invitaciones"));
        const data = querySnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data(),
        }));

        console.log(`✅ Invitaciones obtenidas para ${clienteID}:`, data);
        setInvitaciones(data);
        setListaInvitaciones && setListaInvitaciones(data);
      } catch (error) {
        console.error(`❌ Error al obtener las invitaciones para ${clienteID}:`, error);
      }
    };

    obtenerInvitaciones();
  }, [CATEGORIA_EVENTO, clienteID]);

  const eliminarInvitacion = async (id) => {
    if (!CATEGORIA_EVENTO || !clienteID) {
      console.error("⚠️ No se puede eliminar una invitación sin un evento definido.");
      return;
    }

    try {
      const idNormalizado = id.replace(/\s+/g, "").toLowerCase();
      const docRef = doc(db, CATEGORIA_EVENTO, clienteID, "invitaciones", idNormalizado);
      
      await deleteDoc(docRef);
      console.log(`✅ Invitación con ID ${idNormalizado} eliminada correctamente.`);
      
      setInvitaciones(prev => prev.filter(inv => inv.id !== id));
      setListaInvitaciones(prev => prev.filter(inv => inv.id !== id));
    } catch (error) {
      console.error("❌ Error al eliminar la invitación:", error);
    }
  };

  return (
    <div>
      <h2>Lista de Invitaciones Guardadas</h2>
      {!CATEGORIA_EVENTO || !clienteID ? (
        <p>⚠️ No hay un evento definido para mostrar las invitaciones.</p>
      ) : (
        <table border="1">
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Adultos</th>
              <th>Niños</th>
              <th>Enlace</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {invitaciones.length > 0 ? (
              invitaciones.map((invitacion) => (
                <tr key={invitacion.id}>
                  <td>{invitacion.nombre}</td>
                  <td>{invitacion.invitadosAdultos}</td>
                  <td>{invitacion.invitadosNiños}</td>
                  <td><a href={invitacion.link} target="_blank" rel="noopener noreferrer">Ver Invitación</a></td>
                  <td><button onClick={() => eliminarInvitacion(invitacion.id)}>❌ Eliminar</button></td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5">No hay invitaciones registradas aún.</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default MostrarInvitaciones;
