import React, { useEffect, useRef, useState } from "react";

function AnimacionScroll({ className, children }) {
  const ref = useRef(null);
  const [visible, setVisible] = useState(false);
  const lastScrollTop = useRef(window.scrollY);

  useEffect(() => {
    const observer = new IntersectionObserver(([entry]) => {
      const scrollDown = window.scrollY > lastScrollTop.current;
      lastScrollTop.current = window.scrollY;

      if (scrollDown && entry.isIntersecting) {
        setVisible(true);  // ðŸ”¥ Aparece si el usuario baja
      } else if (!scrollDown) {
        setVisible(false); // ðŸ”¥ Desaparece si el usuario sube
      }
    }, { threshold: 0.5 }); // âœ… Se activa cuando el elemento estÃ© 50% visible

    if (ref.current) observer.observe(ref.current);

    return () => observer.disconnect();
  }, []);

  return (
    <div ref={ref} className={`${className} ${visible ? "visible" : "hidden"}`}>
      {children}
    </div>
  );
}

export default AnimacionScroll;
