import React from "react";

const Confirmacion = ({ invitacion, onConfirmar, onNoAsistir }) => {
  return (
    <section className="foto">
      <div className="style">
        {/* ðŸ”¥ Muestra los datos del invitado */}
        {invitacion && (
          <div 
            className="contasis" 
            style={{ bottom: invitacion.nombre.length > 39 ? "21%" : "28%" }} // Ajuste dinÃ¡mico
          >
            <h4 id="nombreInvitacion" style={{ whiteSpace: invitacion.nombre.length > 39 ? "pre-line" : "nowrap" }}>
              {invitacion.nombre}
            </h4>

            <h4 id="cantidadInvitados">{invitacion.invitados}</h4>
          </div>
        )}

        {/* âœ… Botones de Confirmar y No Asistir */}
        <button className="Confirmar" onClick={onConfirmar}>
          Confirmar Asistencia
        </button>

        <button className="No-asistir" onClick={onNoAsistir}>
          No Asistir
        </button>
      </div>
    </section>
  );
};

export default Confirmacion;
