import React, { useState, useEffect } from "react";
import { setDoc, doc } from "firebase/firestore";
import imagenes from "./assets/imagenes";
import Itinerario from "./Itinerario";
import Countdown from "./Countdown";
import Galeria from "./Galeria";
import BuzonDeDeseos from "./BuzonDeDeseos";
import { db } from "./Database/firebaseConfig"; // ✅ Importa la conexión a Firebase

const Invitacion = () => {
  const [sobreAbierto, setSobreAbierto] = useState(false);
  const [nombre, setNombre] = useState("");

  const manejarConfirmacion = async (asistencia) => {
    if (!nombre.trim()) {
      alert("❌ Por favor, ingresa tu nombre.");
      return;
    }

    const idInvitacion = nombre.replace(/\s+/g, "").toLowerCase();
    const estadoAsistencia = asistencia ? "Asistirá" : "No asistirá";
    const mensajeWhatsApp = asistencia
      ? `Confirmo asistencia. Mi nombre es ${nombre}.`
      : `No puedo asistir. Mi nombre es ${nombre}.`;
    const whatsappLink = `https://wa.me/521XXXXXXXXXX?text=${encodeURIComponent(mensajeWhatsApp)}`;

    try {
      await setDoc(doc(db, "eventos", "invitaciones", idInvitacion), {
        id: idInvitacion,
        nombre,
        asistencia: estadoAsistencia,
        timestamp: new Date(),
      });

      console.log(`✅ Asistencia guardada: ${nombre} - ${estadoAsistencia}`);

      window.open(whatsappLink, "_blank"); // ✅ Abre WhatsApp con el mensaje del usuario
    } catch (error) {
      console.error("❌ Error al guardar la asistencia:", error);
    }
  };

  return (
    <>
      {!sobreAbierto && (
        <div className="sobre">
          <div className="up" id="sobre_hoja1"></div>
          <button className="blason" onClick={() => setSobreAbierto(true)}>
            Abrir Invitación
          </button>
          <div className="down" id="sobre_hoja2"></div>
        </div>
      )}

      {sobreAbierto && (
        <div className="contenido">
          {/* MARK: Confirmación de Asistencia */}
          <section className="confirmacion-asistencia">
            <h3>Confirmar Asistencia</h3>
            <form onSubmit={(e) => e.preventDefault()}>
              <label htmlFor="nombre">Nombre y Apellido:</label>
              <input
                type="text"
                id="nombre"
                value={nombre}
                onChange={(e) => setNombre(e.target.value)}
                placeholder="Escribe tu nombre"
                required
              />
              <div className="botones-confirmacion">
                <button className="boton" onClick={() => manejarConfirmacion(true)}>Confirmar asistencia</button>
                <button className="boton" onClick={() => manejarConfirmacion(false)}>No puedo asistir</button>
              </div>
            </form>
          </section>

          {/* MARK: Otros componentes */}
          <Itinerario />
          <Galeria />
          <BuzonDeDeseos />
        </div>
      )}
    </>
  );
};

export default Invitacion;
