import React from "react";
import imagenes from "./assets/imagenes"; // ✅ Importamos el álbum de imágenes


const eventos = [
  { nombre: "Misa", hora: "3:45 PM", icono: "iglesia2" },
  { nombre: "Recepción", hora: "5:10 PM", icono: "recepcion2" },
  { nombre: "Comida", hora: "5:45 PM", icono: "comida" },
  { nombre: "Vals", hora: "9:30 PM", icono: "vals" },
  { nombre: "Baile", hora: "10:30 PM", icono: "baile" },
  { nombre: "Fin del Evento", hora: "1:00 AM", icono: "fin" }
];

const Separador3 = () => (
    <div className="icono-inv" data-icono="ornamento3"
      style={{
        marginTop: "5%",
        marginBottom: "5%",
        width: "110%",
        maskImage: `url(${imagenes.ornamento3})`,
        WebkitMaskImage: `url(${imagenes.ornamento3})` // ✅ Para compatibilidad en Chrome/Safari
      }}
    ></div>
)

const Itinerario = () => {
  return (
    <div className="itinerario show-p-y">
      <h3>Programa del Evento</h3>
      <Separador3/>
      <div>
        {eventos.map((evento, index) => {
          const posicion = index % 2 === 0 ? "izquierda" : "derecha";
          return (
            <div key={index} className={`evento ${posicion}`}>
              {posicion === "izquierda" ? (
                <>
                  <div className="icono show-n-x">
                    <div className="circulo">
                      <div
                        className="icono-itinerario"
                        style={{
                          WebkitMaskImage: `url(${imagenes[evento.icono]})`,
                          maskImage: `url(${imagenes[evento.icono]})`,
                          backgroundColor: "#fff",
                          width: "70%",
                          height: "70%"
                        }}
                      ></div>
                    </div>
                  </div>
                  <div className="item show-p-x">
                    <h4 className="nombre">{evento.nombre}</h4>
                    <p className="hora">{evento.hora}</p>
                  </div>
                </>
              ) : (
                <>
                  <div className="item show-n-x">
                    <h4 className="nombre">{evento.nombre}</h4>
                    <p className="hora">{evento.hora}</p>
                  </div>
                  <div className="icono show-p-x">
                    <div className="circulo">
                      <div
                        className="icono-itinerario"
                        style={{
                          WebkitMaskImage: `url(${imagenes[evento.icono]})`,
                          maskImage: `url(${imagenes[evento.icono]})`,
                          backgroundColor: "#fff",
                          width: "70%",
                          height: "70%"
                        }}
                      ></div>
                    </div>
                  </div>
                </>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Itinerario;
