import React, { useState, useEffect } from "react";
import axios from "axios";
import { db } from "./Database/firebaseConfig";
import { collection, addDoc, doc, setDoc } from "firebase/firestore";  // ✅ Importación correcta
import FotoSection from "./FotoSection";
import FormGen from "./componentes/FormGen";
import Invitacion from "./Invitacion";
import "./invit.css";
import SendInvitacion from "./Database/SendInvitacion";
import MostrarInvitaciones from "./Database/MostrarInvitaciones";
import generarID from "./Database/generarID";

const App = () => {
  const [nombre, setNombre] = useState("Invitado");
  const [invitadosAdultos, setInvitadosAdultos] = useState(1);
  const [invitadosNiños, setInvitadosNiños] = useState(0);
  const [incluirNiños, setIncluirNiños] = useState(false);
  const [totalInvitados, setTotalInvitados] = useState(1);
  const [linkGenerado, setLinkGenerado] = useState("");
  const [linkAcortado, setLinkAcortado] = useState("");
  const [mostrarMensaje, setMostrarMensaje] = useState(false);


  useEffect(() => {
    if (linkGenerado) {
      setMostrarMensaje(true);

      // 📌 Ocultar el mensaje después de 5 segundos
      const timer = setTimeout(() => {
        setMostrarMensaje(false);
      }, 5000);

      return () => clearTimeout(timer);
    }
  }, [linkGenerado]);

  useEffect(() => {
    const manejarScroll = () => {
      setMostrarMensaje(false); // 📌 Ocultar cuando el usuario hace scroll
    };

    window.addEventListener("scroll", manejarScroll);
    return () => window.removeEventListener("scroll", manejarScroll);
  }, []);

const actualizarDatos = async (nuevoNombre, nuevaCantidadAdultos, nuevaCantidadNiños, incluirNiños) => {
  let nombreLimpio = nuevoNombre.replace(/\s+/g, " ").trim();
  let nombreFinal = nombreLimpio.length > 11 ? nombreLimpio + ",\n" : nombreLimpio;
  const nuevoLink = `https://tuweb.com/invitaciones/${Date.now()}`;

  const totalInvitadosCalculado = nuevaCantidadAdultos + (incluirNiños ? nuevaCantidadNiños : 0);
  const idPersonalizado = await generarID(nombreFinal);  // 📌 Generamos el ID único

  try {
    await setDoc(doc(db, "invitaciones", idPersonalizado), {  // 📌 Usamos `setDoc` para definir el ID manualmente
      nombre: nombreFinal,
      invitadosAdultos: nuevaCantidadAdultos,
      invitadosNiños: incluirNiños ? nuevaCantidadNiños : 0,
      totalInvitados: totalInvitadosCalculado,
      link: nuevoLink,
      timestamp: new Date(),
    });

      console.log(`✅ Invitación guardada con ID: ${idPersonalizado}`);
    } catch (error) {
      console.error("❌ Error al guardar la invitación:", error);
    }
  };




  const handleCompartir = () => {
    if (!linkGenerado) {
      alert("Primero genera la invitación antes de compartirla.");
      return;
    }

    // 📌 Copia el enlace al portapapeles
    navigator.clipboard.writeText(linkGenerado);

    // 📌 Enviar el enlace por WhatsApp
    const mensajeWhatsApp = `¡Hola! Te comparto la invitación: ${linkGenerado}`;
    const urlWhatsApp = `https://api.whatsapp.com/send?phone=+5619060019&text=${encodeURIComponent(mensajeWhatsApp)}`;

    window.open(urlWhatsApp, "_blank");
  };

  const handleGuardar = async () => {
    if (!linkGenerado) {
      alert("Primero genera la invitación antes de guardarla.");
      return;
    }

    try {
      await addDoc(collection(db, "invitaciones"), {
        nombre,
        invitadosAdultos,
        invitadosNiños,
        totalInvitados,
        link: linkGenerado,
        timestamp: new Date(),
      });

      console.log("✅ Invitación guardada en Firebase.");
      alert("✅ Invitación guardada exitosamente.");
    } catch (error) {
      console.error("❌ Error al guardar la invitación en Firebase:", error);
      alert("❌ Hubo un error al guardar la invitación.");
    }
  };


  const copiarAlPortapapeles = () => {
    navigator.clipboard.writeText(linkAcortado || linkGenerado);
    alert("Enlace copiado al portapapeles.");
  };

  return (
    <div>
      {/* 📌 Mostrar el mensaje en un encabezado solo si `mostrarMensaje` está activado */}
      {mostrarMensaje && (
        <header className="invitacion-creada">
          <h1>🎉 Tu invitación ha sido creada 🎉</h1>
        </header>
      )}

      <FormGen 
        actualizarDatos={actualizarDatos} 
        handleCompartir={handleCompartir} 
        linkGenerado={linkGenerado}
      />

      <MostrarInvitaciones />

      <Invitacion nombreInvitacion={nombre} totalInvitadosInvitacion={totalInvitados} />
    </div>
  );
};

export default App;
