import { useParams } from "react-router-dom";

const Showlog = () => {
  const { cliente } = useParams(); // ðŸ‘ˆ Captura desde /login/:cliente
  const [usuario, setUsuario] = useState(null);
  const [cargando, setCargando] = useState(true);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      setUsuario(user);
      setCargando(false);
    });
    return () => unsubscribe();
  }, []);

  if (cargando) return <div>âŒ› Cargando...</div>;

  return usuario ? (
    <App usuario={usuario} nombreCliente={cliente} />
  ) : (
    <Login onLogin={setUsuario} />
  );
};
