import React, { useState } from "react";
import "./FormGen.css";

const FormGen = ({ actualizarDatos }) => {
  if (!actualizarDatos) {
    console.error("Error: La función `actualizarDatos` no fue pasada a `Invitacion`.");
  }

  const [nombre, setNombre] = useState("");
  const [invitados, setInvitados] = useState(1);

  const manejarCambioNombre = (e) => {
    let valor = e.target.value.replace(/\s{2,}/g, " "); // Reemplaza múltiples espacios internos por uno
    
    // Permitir letras, espacios y caracteres especiales ., - /
    if (!/^[a-zA-ZáéíóúÁÉÍÓÚñÑ\s.,\-\/]*$/.test(valor)) {
      alert("Solo puedes introducir texto y los caracteres especiales: . , - /");
      return;
    }
    
    // Mostrar alerta si supera los 20 caracteres
    if (valor.length > 20) {
      alert("El nombre no puede tener más de 20 caracteres.");
      return;
    }
  
    setNombre(valor);
  };

  const manejarCambioInvitados = (e) => {
    let valor = e.target.value;
  
    // Permitir que el usuario borre el campo completamente antes de validar
    if (valor === "") {
      setInvitados(""); // ✅ Permite dejar el input vacío temporalmente
      return;
    }
  
    // Convertir el valor a número y validar solo entre 1 y 25
    let numeroInvitados = parseInt(valor, 2);
    if (isNaN(numeroInvitados) || numeroInvitados < 1 || numeroInvitados > 25) {
      alert("El número de invitados debe estar entre 1 y 25.");
      return;
    }
  
    setInvitados(numeroInvitados);
  };

  // ✅ **Definir `manejarEnvio` antes del return**
  const manejarEnvio = (e) => {
    e.preventDefault();
    actualizarDatos(nombre, invitados);
  };

  return (
    <section id="asistencia">
      <div className="card">
        <h1>Genera tus Invitaciones</h1>
        <h2>Ingresa el número de boletos por invitación así como el nombre de la Familia y/o invitado</h2>
        <form onSubmit={manejarEnvio}>
          <label>Nombre de la Familia:</label>
          <input 
            type="text" 
            value={nombre} 
            onChange={manejarCambioNombre} 
            required 
          />

          <label>Número de Invitados:</label>
          <input 
            type="number" 
            value={invitados} 
            min="1" 
            max="25" 
            onChange={manejarCambioInvitados} 
            required 
          />

          <button type="submit">Generar Invitación</button>
        </form>
      </div>
    </section>
  );
};

export default FormGen;
