import React, { useState, useEffect } from "react";
import axios from "axios";
import { db } from "./Database/firebaseConfig";
import { collection, addDoc } from "firebase/firestore";
import FotoSection from "./FotoSection";
import FormGen from "./componentes/FormGen";
import Invitacion from "./Invitacion";
import imagenes from "./assets/imagenes";
import "./invit.css";
import SendInvitacion from "./Database/SendInvitacion";

const App = () => {
  const [nombre, setNombre] = useState("Invitado");
  const [invitadosAdultos, setInvitadosAdultos] = useState(1);
  const [invitadosNi침os, setInvitadosNi침os] = useState(0);
  const [incluirNi침os, setIncluirNi침os] = useState(false);
  const [totalInvitados, setTotalInvitados] = useState(1);
  const [linkGenerado, setLinkGenerado] = useState("");
  const [linkAcortado, setLinkAcortado] = useState("");
  const [mostrarMensaje, setMostrarMensaje] = useState(false);

  useEffect(() => {
    if (linkGenerado) {
      setMostrarMensaje(true);

      // 游늷 Ocultar el mensaje despu칠s de 5 segundos
      const timer = setTimeout(() => {
        setMostrarMensaje(false);
      }, 5000);

      return () => clearTimeout(timer);
    }
  }, [linkGenerado]);

  useEffect(() => {
    const manejarScroll = () => {
      setMostrarMensaje(false); // 游늷 Ocultar cuando el usuario hace scroll
    };

    window.addEventListener("scroll", manejarScroll);
    return () => window.removeEventListener("scroll", manejarScroll);
  }, []);

  const actualizarDatos = async (nuevoNombre, nuevaCantidadAdultos, nuevaCantidadNi침os, incluirNi침os) => {
    let nombreLimpio = nuevoNombre.replace(/\s+/g, " ").trim();
    let nombreFinal = nombreLimpio.length > 11 ? nombreLimpio + ",\n" : nombreLimpio;
    const idInvitacion = Date.now();
    const nuevoLink = `https://tuweb.com/invitaciones/${idInvitacion}`;

    const totalInvitadosCalculado = nuevaCantidadAdultos + (incluirNi침os ? nuevaCantidadNi침os : 0);

    setNombre(nombreFinal);
    setInvitadosAdultos(nuevaCantidadAdultos);
    setInvitadosNi침os(incluirNi침os ? nuevaCantidadNi침os : 0);
    setTotalInvitados(totalInvitadosCalculado);
    setLinkGenerado(nuevoLink);

    try {
      await addDoc(collection(db, "invitaciones"), {
        id: idInvitacion,
        nombre: nombreFinal,
        invitadosAdultos: nuevaCantidadAdultos,
        invitadosNi침os: incluirNi침os ? nuevaCantidadNi침os : 0,
        totalInvitados: totalInvitadosCalculado,
        link: nuevoLink,
        timestamp: new Date(),
      });

      const response = await axios.get(`https://tinyurl.com/api-create.php?url=${nuevoLink}`);
      setLinkAcortado(response.data);
    } catch (error) {
      console.error("Error al guardar la invitaci칩n:", error);
    }
  };

  const handleCompartir = () => {
    if (!linkGenerado) {
      alert("Primero genera la invitaci칩n antes de compartirla.");
      return;
    }

    // 游늷 Copia el enlace al portapapeles
    navigator.clipboard.writeText(linkGenerado);

    // 游늷 Enviar el enlace por WhatsApp
    const mensajeWhatsApp = `춰Hola! Te comparto la invitaci칩n: ${linkGenerado}`;
    const urlWhatsApp = `https://api.whatsapp.com/send?phone=+5619060019&text=${encodeURIComponent(mensajeWhatsApp)}`;

    window.open(urlWhatsApp, "_blank");
  };

  const copiarAlPortapapeles = () => {
    navigator.clipboard.writeText(linkAcortado || linkGenerado);
    alert("Enlace copiado al portapapeles.");
  };

  return (
    <div>
      {/* 游늷 Mostrar el mensaje en un encabezado solo si `mostrarMensaje` est치 activado */}
      {mostrarMensaje && (
        <header className="invitacion-creada">
          <h1>游꿀 Tu invitaci칩n ha sido creada 游꿀</h1>
        </header>
      )}

      <FormGen 
        actualizarDatos={actualizarDatos} 
        handleCompartir={handleCompartir} 
        linkGenerado={linkGenerado}
      />

      <Invitacion nombreInvitacion={nombre} totalInvitadosInvitacion={totalInvitados} />

      {linkGenerado && (
        <div>
          <p>Tu invitaci칩n ha sido generada</p>
          <button onClick={copiarAlPortapapeles}>Copiar enlace</button>
        </div>
      )}
    </div>
  );
};

export default App;
