import React, { useState, useEffect } from "react";
import { collection, getDocs, deleteDoc, doc } from "firebase/firestore";
import { db } from "../Database/firebaseConfig";

const MostrarInvitaciones = () => {
  const [invitaciones, setInvitaciones] = useState([]);

  const obtenerInvitaciones = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "invitaciones"));
      const data = querySnapshot.docs.map(doc => ({
        id: doc.id,  // üìå Usamos la ID generada por Firestore
        ...doc.data(),
      }));
      setInvitaciones(data);
    } catch (error) {
      console.error("‚ùå Error al obtener las invitaciones:", error);
    }
  };

  useEffect(() => {
    obtenerInvitaciones();
  }, []);

  const eliminarInvitacion = async (docId) => {
    try {
      await deleteDoc(doc(db, "invitaciones", docId)); // üìå Usa la ID del documento generado por Firebase
      console.log(`‚úÖ Invitaci√≥n con ID ${docId} eliminada correctamente.`);
  
      // üìå Filtrar los datos actualizados despu√©s de eliminar
      setInvitaciones((prevInvitaciones) => prevInvitaciones.filter((inv) => inv.id !== docId));
    } catch (error) {
      console.error("‚ùå Error al eliminar la invitaci√≥n:", error);
    }
  };
  

  return (
    <div>
      <h2>Lista de Invitaciones Guardadas</h2>
      <table border="1">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Adultos</th>
            <th>Ni√±os</th>
            <th>Enlace</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {invitaciones.length > 0 ? (
            invitaciones.map((invitacion) => (
              <tr key={invitacion.id}>
                <td>{invitacion.nombre}</td>
                <td>{invitacion.invitadosAdultos}</td>
                <td>{invitacion.invitadosNi√±os}</td>
                <td><a href={invitacion.link} target="_blank" rel="noopener noreferrer">Ver Invitaci√≥n</a></td>
                <td><button onClick={() => eliminarInvitacion(invitacion.id)}>‚ùå Eliminar</button></td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">No hay invitaciones registradas a√∫n.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default MostrarInvitaciones;
