import React, { useState, getDoc, ref } from "react";
import { db } from "./firebaseConfig";

import imagenes from "../assets/imagenes";
import { collection, doc, getDocs, setDoc } from "firebase/firestore";
import { signInWithEmailAndPassword, signOut, createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "./firebaseConfig";
import "./Login.css"

const Login = ({ onLogin, setVerificandoSesion, setUsuarioActual }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mostrarPassword, setMostrarPassword] = useState(false);
  

  // ✅ Buscar ruta personalizada en Firestore
  const obtenerDatosCliente = async (email) => {
    const categorias = ["XV Años", "Eventos Empresariales", "Bodas"];

    for (const categoria of categorias) {
      const clientesRef = collection(db, "Clientes", "Categorias", categoria);
      const snapshot = await getDocs(clientesRef);

      for (const docSnap of snapshot.docs) {
        const data = docSnap.data();
        if (data.correo === email) {
          return {
            rutaPersonalizada: data.rutaPersonalizada,
            fechaLimite: data.fechaLimite
          };
        }
      }
    }

    throw new Error("Cliente no registrado en Firestore.");
  };


  const handleLogin = async () => {
  try {
    setVerificandoSesion(true); // ✅ Activa el estado de carga

    const userCred = await signInWithEmailAndPassword(auth, email, password);
    const user = userCred.user;
    const { rutaPersonalizada, fechaLimite } = await obtenerDatosCliente(user.email);

    const clienteSnap = await getDoc(ref);
    const clienteData = clienteSnap.data();

    const ahora = new Date();
    const limite = new Date(clienteData.fechaLimite);
    const accesoPermitido = ahora <= limite;

    const setUsuarioActual = {
      ...user, // datos del auth: uid, email, etc.
      ...clienteData, // datos guardados en Firestore
      accesoPermitido // ✅ acceso basado en fecha
    };

    setUsuarioActual(user);       // ✅ Actualiza el estado
    onLogin(user);                // ✅ Notifica al padre si es necesario
    setVerificandoSesion(false); // ✅ Apaga la pantalla de carga

    window.location.href = rutaPersonalizada; // 🔀 Redirige al usuario
  } catch (err) {
    alert("❌ El correo o contraseña son incorrectos: ");
    setVerificandoSesion(false); // ❌ Evita que se quede en modo cargando
  }
};


  const handleLogout = () => {
    signOut(auth).then(() => {
      alert("👋 Sesión cerrada");
      onLogin(null);
    });
  };

  return (
  <div className="fondo-login">
    <div className="wrapper">
      <form onSubmit={(e) => {
          e.preventDefault();
          handleLogin();
        }}>
        <h2>Iniciar sesión</h2>

        <div className="input-field">
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <label>Correo</label>
        </div>

        {/* Campo de contraseña con ícono en esquina */}
        <div className="input-field password-field">
          <input
            type={mostrarPassword ? "text" : "password"}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <label>Contraseña</label>
          <span
            className="toggle-password"
            onClick={() => setMostrarPassword((prev) => !prev)}
          >
            <i className={`fa-solid ${mostrarPassword ? "fa-eye" : "fa-eye-slash"}`}></i>
          </span>
        </div>

        <div className="forget">
          <a href="#">¿Olvidaste tu contraseña?</a>
        </div>

        <button className="login" type="submit" onClick={handleLogin}>
          Iniciar sesión
        </button>
      </form>
    </div>
  </div>
  );
};

export default Login;
