import React, { useState } from "react";
import axios from "axios";
import { db } from "./Database/firebaseConfig";
import { collection, addDoc, getDocs } from "firebase/firestore";
import FotoSection from "./FotoSection";
import FormGen from "./componentes/FormGen";
import imagenes from "./assets/imagenes";
import "./invit.css";

const App = () => {
  const [nombre, setNombre] = useState("Invitado");
  const [invitados, setInvitados] = useState(1);
  const [tipoDiseño, setTipoDiseño] = useState("RJ"); // Diseño predeterminado
  const [linkGenerado, setLinkGenerado] = useState("");
  const [linkAcortado, setLinkAcortado] = useState("");

  // 🆕 Función para generar un ID autoincrementable
  const generarIDInvitacion = async (tipoDiseño, nombreCliente) => {
    const querySnapshot = await getDocs(collection(db, "invitaciones"));
    const cantidadUsada = querySnapshot.docs.filter(doc => doc.data().tipoDiseño === tipoDiseño).length + 1;
    return `${tipoDiseño}${cantidadUsada}${nombreCliente.replace(/\s+/g, "")}`;
  };

  // 📌 Función para actualizar datos y guardar en Firebase
  const actualizarDatos = async (nuevoNombre, nuevaCantidad, nuevoDiseño) => {
    setTipoDiseño(nuevoDiseño);
    let nombreLimpio = nuevoNombre.replace(/\s+/g, " ").trim();
    let nombreFinal = nombreLimpio.length > 11 ? nombreLimpio + ",\n" : nombreLimpio;
    const idInvitacion = await generarIDInvitacion(nuevoDiseño, nuevoNombre);
    const nuevoLink = `https://tuweb.com/invitaciones/${idInvitacion}`;
    setLinkGenerado(nuevoLink);

    try {
      await addDoc(collection(db, "invitaciones"), {
        id: idInvitacion,
        tipoDiseño: nuevoDiseño,
        nombre: nombreFinal,
        invitados: nuevaCantidad,
        link: nuevoLink,
        timestamp: new Date(),
      });

      // 📌 Acortar el enlace con TinyURL
      const response = await axios.get(`https://tinyurl.com/api-create.php?url=${nuevoLink}`);
      setLinkAcortado(response.data);
    } catch (error) {
      console.error("Error al guardar la invitación:", error);
    }
  };

  // 📌 Función para copiar el enlace
  const copiarAlPortapapeles = () => {
    navigator.clipboard.writeText(linkAcortado || linkGenerado);
    alert("Enlace copiado al portapapeles.");
  };

  return (
    <div>
      <FormGen actualizarDatos={actualizarDatos} />

      {linkGenerado && (
        <div>
          <p>
            Tu invitación ha sido generada:{" "}
            <a href={linkAcortado || linkGenerado} target="_blank" rel="noopener noreferrer">
              {linkAcortado || linkGenerado}
            </a>
          </p>
          <button onClick={copiarAlPortapapeles}>📋 Copiar enlace</button>
        </div>
      )}

      <FotoSection
        imageSrc={imagenes.img9}
        buttonText1="Confirmar asistencia"
        buttonLink1="https://api.whatsapp.com/send?phone=+525619060019&text=Gracias por invitarme!"
        buttonText2="No puedo asistir"
        buttonLink2="https://api.whatsapp.com/send?phone=+525619060019&text=No podre asistir."
        invitacion={{
          nombre: `${nombre} estás invitado a mi Fiesta`,
          invitados: `Invitación para ${invitados} adulto(s)`,
          diseño: `Diseño: ${tipoDiseño}`,
        }}
      />
    </div>
  );
};

export default App;
