import React, { useState } from "react";
import imagenes from "./assets/imagenes"; // Importación correcta
import "./Invit3.css"; // Archivo CSS para estilos

const Invitacion3 = () => {
    const [sobreAbierto, setSobreAbierto] = useState(false);

    return (
        <>
            {!sobreAbierto ? (
                <div className="sobre">
                    <div className="up" id="sobre_hoja1"></div>
                    <button className="blason" onClick={() => setSobreAbierto(true)}>
                        Abrir Invitación
                    </button>
                    <div className="down" id="sobre_hoja2"></div>
                </div>
            ) : (
                <div className="contenido">
                    {/* MARK: Decoraciones */}
                    <div className="top">
                        <img className="top-img" src={imagenes.iglesia} alt="Decoración superior" />
                    </div>
                    <div className="bottom">
                        <img className="bottom-img" src={imagenes.iglesia} alt="Decoración inferior" />
                    </div>
                </div>
            )}
        </>
    );
};

export default Invitacion3;
