import React, { useEffect, useRef, useState } from "react";

function AnimacionScroll({ className, children }) {
  const ref = useRef(null);
  const [visible, setVisible] = useState(false);
  const lastScrollY = useRef(window.scrollY);
  const lastVisible = useRef(false);

  useEffect(() => {
    const observer = new IntersectionObserver(([entry]) => {
      const scrollDown = window.scrollY > lastScrollY.current;
      lastScrollY.current = window.scrollY;

      // ðŸ”¥ Detecta si el elemento entra en pantalla desde abajo
      if (scrollDown && entry.boundingClientRect.top < window.innerHeight && entry.isIntersecting && !lastVisible.current) {
        setVisible(true); // âœ… Activa la animaciÃ³n
        lastVisible.current = true;
      }

      // ðŸ”¥ Detecta si el elemento sale completamente por la parte inferior
      else if (!scrollDown && entry.boundingClientRect.top > window.innerHeight) {
        setVisible(false); // âœ… Desactiva para permitir la reactivaciÃ³n al bajar nuevamente
        lastVisible.current = false;
      }
    }, { threshold: 0.5 });

    if (ref.current) observer.observe(ref.current);

    return () => observer.disconnect();
  }, []);

  return (
    <div ref={ref} className={`${className} ${visible ? getAnimationClass(className) : "hidden"}`}>
      {children}
    </div>
  );
}

// ðŸ”¥ FunciÃ³n para asignar animaciones segÃºn la clase recibida
function getAnimationClass(className) {
  if (className.includes("show-n-x")) return "active-n-x";
  if (className.includes("show-p-x")) return "active-p-x";
  if (className.includes("show-n-y")) return "active-n-y";
  if (className.includes("show-p-y")) return "active-p-y";
  if (className.includes("show")) return "active";
  return "visible"; // Default si no coincide con ninguna
}

export default AnimacionScroll;
