import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { db } from "./Database/firebaseConfig";
import { collection, doc, setDoc, addDoc } from "firebase/firestore";
import FotoSection from "./FotoSection";
import FormGen from "./componentes/FormGen";
import Invitacion from "./Invitacion";
import imagenes from "./assets/imagenes";
import "./invit.css";
import SendInvitacion from "./Database/SendInvitacion";
import MostrarInvitaciones from "./Database/MostrarInvitaciones";

const App = () => {
  const [nombre, setNombre] = useState(() => {
    return localStorage.getItem("nombreCliente") || "Invitado";
  });
  const [invitadosAdultos, setInvitadosAdultos] = useState(1);
  const [invitadosNiÃ±os, setInvitadosNiÃ±os] = useState(0);
  const [incluirNiÃ±os, setIncluirNiÃ±os] = useState(false);
  const [totalInvitados, setTotalInvitados] = useState(1);
  const [linkGenerado, setLinkGenerado] = useState("");
  const [linkAcortado, setLinkAcortado] = useState("");
  const [mostrarMensaje, setMostrarMensaje] = useState(false);
  const [listaInvitaciones, setListaInvitaciones] = useState([]);
  const [clienteNombre, setClienteNombre] = useState("");


  useEffect(() => {
    if (linkGenerado) {
      setMostrarMensaje(true);

      // ðŸ“Œ Ocultar el mensaje despuÃ©s de 5 segundos
      const timer = setTimeout(() => {
        setMostrarMensaje(false);
      }, 5000);

      return () => clearTimeout(timer);
    }
  }, [linkGenerado]);

  useEffect(() => {
    const manejarScroll = () => {
      setMostrarMensaje(false); // ðŸ“Œ Ocultar cuando el usuario hace scroll
    };

    window.addEventListener("scroll", manejarScroll);
    return () => window.removeEventListener("scroll", manejarScroll);
  }, []);

    const CATEGORIA_EVENTO = "Pedas Nocturnas";  // ðŸ”„ Puedes cambiar esto a "XV AÃ±os", "Eventos Empresariales", etc.
    const NOMBRE_EVENTO = "Dana Yamilet Ortega";  // ðŸ”„ Nombre especÃ­fico del evento

  const actualizarDatos = async (nombreCliente, nuevoNombre, nuevaCantidadAdultos, nuevaCantidadNiÃ±os, incluirNiÃ±os) => {
    if (!nombreCliente || typeof nombreCliente !== "string" || nombreCliente.trim() === "") {
        console.error("âŒ Error: `nombreCliente` no tiene un valor vÃ¡lido.");
        return;
    }

    let nombreLimpio = nuevoNombre.replace(/\s+/g, " ").trim();
    const idInvitacion = nombreCliente.replace(/\s+/g, "").toLowerCase();  // âœ… Normaliza el ID (sin espacios y en minÃºsculas)
    const nuevoLink = `https://tuweb.com/invitaciones/${idInvitacion}`;
    const totalInvitadosCalculado = nuevaCantidadAdultos + (incluirNiÃ±os ? nuevaCantidadNiÃ±os : 0);

    // ðŸ“Œ Corregir la asignaciÃ³n de estados
    setNombre(nombreCliente);
    console.log("âœ… Estado actualizado en App.js:", nombreCliente);
    setInvitadosAdultos(nuevaCantidadAdultos);
    setInvitadosNiÃ±os(nuevaCantidadNiÃ±os);
    setTotalInvitados(totalInvitadosCalculado);
    setLinkGenerado(nuevoLink);
    setMostrarMensaje(true);
    setTimeout(() => setMostrarMensaje(false), 5000);

    try {
        await setDoc(doc(db, CATEGORIA_EVENTO, NOMBRE_EVENTO, "invitaciones", idInvitacion), {  // âœ… Guarda en la categorÃ­a dinÃ¡mica
            id: idInvitacion,  
            nombre: nombreCliente,
            invitadosAdultos: nuevaCantidadAdultos,
            invitadosNiÃ±os: nuevaCantidadNiÃ±os,
            totalInvitados: totalInvitadosCalculado,
            categoria: CATEGORIA_EVENTO,  // âœ… Guarda la categorÃ­a en cada invitaciÃ³n
            link: nuevoLink,
            timestamp: new Date(),
        });

        console.log(`âœ… InvitaciÃ³n guardada en '${CATEGORIA_EVENTO}/${NOMBRE_EVENTO}/invitaciones/${idInvitacion}'`);

        const response = await axios.get(`https://tinyurl.com/api-create.php?url=${nuevoLink}`);
        setLinkAcortado(response.data);
    } catch (error) {
        console.error("âŒ Error al guardar la invitaciÃ³n:", error);
    }
  };

  const handleCompartir = () => {
    if (!linkGenerado) {
      alert("Primero genera la invitaciÃ³n antes de compartirla.");
      return;
    }

    // ðŸ“Œ Copia el enlace al portapapeles
    navigator.clipboard.writeText(linkGenerado);

    // ðŸ“Œ Enviar el enlace por WhatsApp
    const mensajeWhatsApp = `Â¡Hola! Te comparto la invitaciÃ³n: ${linkGenerado}`;
    const urlWhatsApp = `https://api.whatsapp.com/send?phone=+5619060019&text=${encodeURIComponent(mensajeWhatsApp)}`;

    window.open(urlWhatsApp, "_blank");
  };

  const copiarAlPortapapeles = () => {
    navigator.clipboard.writeText(linkAcortado || linkGenerado);
    alert("Enlace copiado al portapapeles.");
  };

  return (
    <div>
      {/* ðŸ“Œ Mostrar el mensaje en un encabezado solo si `mostrarMensaje` estÃ¡ activado */}
      {mostrarMensaje && (
        <header className="invitacion-creada">
          <h1>ðŸŽ‰ Tu invitaciÃ³n ha sido creada ðŸŽ‰</h1>
        </header>
      )}


      <FormGen 
        actualizarDatos={actualizarDatos} 
        handleCompartir={handleCompartir} 
        linkGenerado={linkGenerado}
      />

      <MostrarInvitaciones nombreCliente={nombre} setListaInvitaciones={setListaInvitaciones} />

      <Invitacion nombreInvitacion={nombre} totalInvitadosInvitacion={totalInvitados} />
    </div>
  );
};

export default App;
