import React, { useState } from "react";
import { db } from "./firebaseConfig";
import { doc, getDoc, setDoc } from "firebase/firestore";
import { signInWithEmailAndPassword, signOut, createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "./firebaseConfig";


const Login = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mostrarPassword, setMostrarPassword] = useState(false);
  const [nombreCliente, setNombreCliente] = useState("");
    const [categoria, setCategoria] = useState("");


  // ‚úÖ Buscar ruta personalizada en Firestore
  const obtenerRutaPersonalizada = async (email, categoria, nombreCliente) => {
    const idSeguro = email.replace(/[@.]/g, "_");
    const ref = doc(db, "Clientes", "Categorias", categoria, nombreCliente);
    const snap = await getDoc(ref);

    if (snap.exists()) {
      return snap.data().rutaPersonalizada;
    } else {
      throw new Error("Cliente no registrado en Firestore.");
    }
  };

  const registrarCliente = async (nombreCliente, categoria, correo) => {
    const ref = doc(db, "Clientes", "Categorias", categoria, nombreCliente);

    await setDoc(ref, {
        nombreCliente,
        categoria,
        correo,
        rutaPersonalizada: `/${categoria}/${nombreCliente}/invitaciones`
    });

    console.log(`‚úÖ Cliente guardado en /Clientes/Categorias/${categoria}/${nombreCliente}`);
    };


  const handleLogin = async () => {
    try {
      const userCred = await signInWithEmailAndPassword(auth, email, password);
      const userEmail = userCred.user.email;
      const ruta = await obtenerRutaPersonalizada(userEmail);

      onLogin(userCred.user);
      window.location.href = ruta;
    } catch (err) {
      alert("‚ùå Error al iniciar sesi√≥n o encontrar la ruta: " + err.message);
    }
  };

  const handleLogout = () => {
    signOut(auth).then(() => {
      alert("üëã Sesi√≥n cerrada");
      onLogin(null);
    });
  };

  // üîÑ Solo registra el usuario, no la ruta personalizada. Eso lo puedes hacer por separado.
  const handleRegister = async () => {
    try {
        const userCred = await createUserWithEmailAndPassword(auth, email, password);
        onLogin(userCred.user);
        alert("üéâ Registro exitoso");

        // üîÑ Aqu√≠ registramos el cliente en Firestore
        await registrarCliente(email, categoria, nombreCliente);
    } catch (err) {
        alert("‚ùå Error al registrarse: " + err.message);
    }
    };


  return (
    <div>
        <input
            value={nombreCliente}
            onChange={(e) => setNombreCliente(e.target.value)}
            placeholder="Nombre del Cliente (ej. Briseyda)"
        />
        <input
            value={categoria}
            onChange={(e) => setCategoria(e.target.value)}
            placeholder="Categor√≠a del Evento (ej. XV A√±os)"
        />
        <input
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Correo"
        />
        <input
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Contrase√±a"
            type={mostrarPassword ? "text" : "password"}
        />
        <button onClick={() => setMostrarPassword((prev) => !prev)}>
            {mostrarPassword ? "üôà Ocultar" : "üëÅÔ∏è Ver"}
        </button>
        <button onClick={handleLogin}>Iniciar sesi√≥n</button>
        <button onClick={handleRegister}>Registrarse</button>
    </div>

  );
};

export default Login;
