import React, { useEffect, useRef, useState } from "react";

function AnimacionScroll({ className, children }) {
  const ref = useRef(null);
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        setVisible(entry.isIntersecting); // 游댠 Activa la animaci칩n cada vez que aparece
      },
      { threshold: 0.3 } // 游댠 Se activa cuando el elemento est치 30% visible
    );

    if (ref.current) observer.observe(ref.current);

    return () => observer.disconnect();
  }, []);

  return (
    <div ref={ref} className={`${className} ${visible ? "visible" : ""}`}>
      {children}
    </div>
  );
}

export default AnimacionScroll;
