import React, { useState, useEffect  } from "react";
import { db } from "./firebaseConfig";

import imagenes from "../assets/imagenes";
import { collection, doc, getDocs, setDoc } from "firebase/firestore";
import { signInWithEmailAndPassword, signOut, createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "./firebaseConfig";
import "./Login.css"

const Login = ({ onLogin, setVerificandoSesion, setUsuarioActual }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mostrarPassword, setMostrarPassword] = useState(false);
  const [mensajeError, setMensajeError] = useState("");

  useEffect(() => {
  setMensajeError("⛔ Mensaje de prueba");
}, []);

  

  // ✅ Buscar ruta personalizada en Firestore
  const obtenerRutaPersonalizada = async (email) => {
    const categorias = ["XV Años", "Eventos Empresariales", "Bodas"]; // o hazlo dinámico

    for (const categoria of categorias) {
        const clientesRef = collection(db, "Clientes", "Categorias", categoria);
        const snapshot = await getDocs(clientesRef);

        for (const docSnap of snapshot.docs) {
        const data = docSnap.data();
        if (data.correo === email) {
            return data.rutaPersonalizada;
        }
        }
    }

    throw new Error("Cliente no registrado en Firestore.");
  };

  const handleLogin = async () => {
  setVerificandoSesion(true);
  setMensajeError("");

  try {
    const userCred = await signInWithEmailAndPassword(auth, email, password);
    const user = userCred.user;

    const ruta = await obtenerRutaPersonalizada(user.email);

    setUsuarioActual(user);
    onLogin(user);
    setVerificandoSesion(false);

    // ✅ Redirigir después para evitar que se interrumpa el render
    setTimeout(() => {
      window.location.href = ruta;
    }, 500);

  } catch (err) {
    let mensaje = "❌ Error al iniciar sesión.";
    if (err.code === "auth/user-not-found") {
      mensaje = "El correo no está registrado.";
    } else if (err.code === "auth/wrong-password") {
      mensaje = "La contraseña es incorrecta.";
    } else if (err.code === "auth/invalid-email") {
      mensaje = "Formato de correo inválido.";
    }

    setMensajeError(mensaje);         // ✅ Este es el mensaje que aparece
    setVerificandoSesion(false);      // ✅ Apaga el loading
  }
};


  const handleLogout = () => {
    signOut(auth).then(() => {
      alert("👋 Sesión cerrada");
      onLogin(null);
    });
  };

  return (
  <div className="fondo-login">
    <div className="wrapper">
      <form onSubmit={(e) => {
          e.preventDefault();
          handleLogin();
        }}>
        <h2>Iniciar sesión</h2>
        {mensajeError && <p className="errmesage">{mensajeError}</p>}

        <div className="input-field">
          <input
            type="email"
            value={email}
            onChange={(e) => { setEmail(e.target.value); setMensajeError("");}}
            required
          />
          <label>Correo</label>
        </div>

        {/* Campo de contraseña con ícono en esquina */}
        <div className="input-field password-field">
          <input
            type={mostrarPassword ? "text" : "password"}
            value={password}
            onChange={(e) => { setPassword(e.target.value); setMensajeError("");}}
            required
          />
          <label>Contraseña</label>
          <span
            className="toggle-password"
            onClick={() => setMostrarPassword((prev) => !prev)}
          >
            <i className={`fa-solid ${mostrarPassword ? "fa-eye" : "fa-eye-slash"}`}></i>
          </span>
        </div>

        <div className="forget">
          <a href="#">¿Olvidaste tu contraseña?</a>
        </div>

        <button className="login" type="submit">
          Iniciar sesión
        </button>
      </form>
    </div>
  </div>
  );
};

export default Login;
